import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
    root: {
        position: "relative",
        height: "100%",
        width: "100%",
        display: "flex",
        flexDirection: "column",
        [theme.breakpoints.up("md")]: {

            alignItems: "center",
        },
        "& p": {
            textAlign: "center"
        },
        "& a": {
            textAlign: "center"
        },
    },
    title: {
        margin: theme.spacing(1),
        textAlign: "center",
    },
    subTitle: {
        margin: theme.spacing(1),
        ['strong']: {
            fontWeight: 700,
        }
    },
    buttonContainer: {
        // marginTop: theme.spacing(1),
        marginBottom: theme.spacing(4),
        width: "100%"
    },
    logo: {
        marginBottom: theme.spacing(2),
    },
    changeEmail: {
        cursor: "pointer",
    },
    subTitleLinkChangeEmail: {
        marginTop: '8px',
        marginBottom: "4px",
        display: "block",
        textAlign: "center",
        fontWeight: "bold",
    },
    email: {
        fontWeight: 700,
    }
}));
export default useStyles;
