import React, { useEffect } from "react";
import Slider from "@material-ui/core/Slider";
import withStyles from "@material-ui/core/styles/withStyles";
import PropTypes from "prop-types";
import CustomTypography from "../Typography";
import { styles } from "./styles";

const SliderExample = ({
    classes,
    values,
    handleChange,
    prefix,
    sufix,
    min,
    max,
    step,
    showValue,
    onChangeCommitted,
}) => {
    const [value, setValue] = React.useState(values);

    useEffect(() => {
        setValue(values);
    }, [values]);

    const onChange = (event, newValue) => {
        setValue(newValue);
        if (handleChange) handleChange(newValue);
    };

    return (
        <div>
            <div className={classes.textContainer}>
                {showValue &&
          value &&
          value.map((item, i) => (
              // eslint-disable-next-line react/no-array-index-key
              <CustomTypography type="captionregular" key={i}>
                  {prefix} {item} {sufix}
              </CustomTypography>
          ))}
            </div>
            <Slider
                value={value}
                onChange={onChange}
                onChangeCommitted={onChangeCommitted}
                aria-labelledby="range-slider"
                min={min}
                max={max}
                step={step}
                classes={{
                    thumb: classes.thumb,
                    valueLabel: classes.valueLabel,
                    root: classes.root,
                }}
            />
        </div>
    );
};

SliderExample.propTypes = {
    classes: PropTypes.oneOfType([PropTypes.object, PropTypes.string]).isRequired,
    showValue: PropTypes.bool,
    values: PropTypes.oneOfType([PropTypes.array, PropTypes.object]),
    prefix: PropTypes.string,
    sufix: PropTypes.string,
    min: PropTypes.number,
    max: PropTypes.number,
};

SliderExample.defaultProps = {
    showValue: false,
};

export default withStyles(styles, { withTheme: true })(SliderExample);
