export const styles = theme => ({
    root: {
        borderRadius: "8px",
        boxSizing: "border-box",
        fontFamily: theme.font.regular,
        padding: `${theme.spacing(1)}px 0`,
        position: "relative",
        width: "100%",
        [theme.breakpoints.between("xs", "md")]: {
            padding: `${theme.spacing(0.5)}px 0`,
        },
    },
    small: {
        border: `1px solid ${theme.palette.grey[50]}`,
        borderRadius: "8px",
        boxSizing: "border-box",
        fontFamily: theme.font.regular,
        fontSize: "14px",
        height: theme.spacing(5),
        outline: "none",
        padding: `${theme.spacing(2)}px ${theme.spacing(1)}px`,
        position: "relative",
        textIndent: theme.spacing(1),
        width: "100%",
        color: "#999999",
    },
    large: {
        border: `1px solid ${theme.palette.grey[50]}`,
        borderRadius: "8px",
        boxSizing: "border-box",
        fontFamily: theme.font.regular,
        fontSize: "14px",
        height: theme.spacing(7),
        outline: "none",
        padding: `${theme.spacing(2)}px ${theme.spacing(1)}px`,
        position: "relative",
        textIndent: theme.spacing(2),
        width: "100%",
    },
    inputIcon: { textIndent: theme.spacing(5) },
    inputActive: {
        background: theme.palette.white.main,
        borderColor: theme.palette.primary.main,
    },
    disabled: {
        background: theme.palette.text.disabled,
        borderColor: theme.palette.text.disabled,
        color: theme.palette.text.secondary,
        pointerEvents: "none",
    },
    readonly: {
        pointerEvents: "none",
    },
    label: {
        color: `${theme.palette.text.primary} `,
        fontSize: "14px",
        left: 0,
        pointerEvents: "none",
        position: "absolute",
        top: 0,
        transform: `translate(${theme.spacing(2)}px, 19px) scale(1)`,
        transition:
            "color 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms,transform 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms",
        zIndex: theme.level.content,
    },
    labelIcon: { transform: "translate(42px, 19px) scale(1)" },
    labelLarge: {
        color: "#CACACA ",
        fontSize: "14px",
        left: 0,
        pointerEvents: "none",
        position: "absolute",
        top: 0,
        transform: `translate(${theme.spacing(2)}px, 27px) scale(1)`,
        transition:
            "color 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms,transform 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms",
        zIndex: theme.level.content,
        [theme.breakpoints.between("xs", "md")]: {
            transform: `translate(${theme.spacing(2)}px, 22px) scale(1)`,
        },
    },
    labelLargeIcon: {
        transform: `translate(${theme.spacing(5.5)}px, 27px) scale(1)`,
        [theme.breakpoints.between("xs", "md")]: {
            transform: `translate(${theme.spacing(5.5)}px, 22px) scale(1)`,
        },
    },
    lblActive: {
        background: theme.palette.white.main,
        color: theme.palette.primary.main,
        padding: "0 5px",
        transform: "translate(16px, -6px) scale(1)",
    },
    lblSemiActive: {
        background: theme.palette.white.main,
        color: theme.palette.primary.main,
        padding: "0 5px",
    },
    disabledLabel: { color: `${theme.palette.text.secondary} ` },
    error: { border: `1px solid ${theme.palette.error.main}` },
    errorText: {
        bottom: "-14px",
        color: "#FFFFFF",
        fontFamily: theme.font.semibold,
        fontSize: "12px ",
        left: 4,
        position: "absolute",
    },
    hasError: { marginBottom: theme.spacing(2) },
    icon: {
        left: theme.spacing(2),
        position: "absolute",
        top: "50%",
        transform: "translateY(-50%)",
        zIndex: 2,
    },
    iconNoBorder: {
        paddingRight: theme.spacing(1),
        "&:after": {
            borderRight: `1px solid ${theme.palette.grey[50]}`,
            bottom: 0,
            content: "\"\"",
            position: "absolute",
            right: 0,
            top: 0,
        },
    },
    noBorder: {
        borderRadius: "8px",
        border: "none",
    },
    transparent: {
        background: theme.palette.primary.alphaDark,
        color: theme.palette.white.main,
        "& fieldset": { border: "none" },
    },
    lbtransparent: {
        color: "rgba(255, 255, 255, 0.7)",
        background: "transparent",
    },
    lbBold: {
        fontFamily: theme.font.semibold,
        fontWeight: "bold",
    },
    specialOutline: { border: "none" },
});
