import React, { ReactNode, useEffect, useRef } from "react"
import Typography from "@material-ui/core/Typography";
import { useField } from "@unform/core";
import MessageValidation from "../MessageValidation";
import useStyles from  "./styles"

interface CheckboxProps {
    name: string
    value: any
    label: string | ReactNode
}
const Checkbox: React.FC<CheckboxProps> = ({ name, value, label, ...rest }) =>  {
    const classes = useStyles()
    const errorMessageRef = useRef<HTMLInputElement>(null);
    const inputRef = useRef<any>(null)
    const { fieldName, defaultValue, registerField, error } = useField(name)

    const defaultChecked = defaultValue === value

    useEffect(() => {
        registerField({
            name: fieldName,
            ref: inputRef,
            getValue: (ref) => {
                return ref.current.checked
            },
            clearValue: (ref) => {
                ref.current.checked = defaultChecked
            },
            setValue: (ref, value) => {
                ref.current.checked = value
            },
        })
    }, [defaultValue, fieldName, registerField, defaultChecked])

    return (
        <div style={{display: "flex",  marginTop: 10 }}>
            <input
                defaultChecked={defaultChecked}
                ref={inputRef}
                value={value}
                type="checkbox"
                id={fieldName}
                {...rest}
            />

            <Typography variant="subtitle2" className={classes.textCheckbox} key={fieldName}>
                {label}
                {error && (
                    <MessageValidation ref={errorMessageRef} isError message={error} />
                )}
            </Typography>
            <div />

        </div>
    )
}

export default Checkbox
