import React from "react";
import PropTypes from "prop-types";
import Checkbox from "../Checkbox";
import Filter from "../Filter";

const FilterOption = ({
    filter,
    items,
    sufix,
    onChange,
    isMobile,
    className,
    noModal,
    label,
    open,
    onOpen,
    onClose,
    onApply,
    onReset,
    onActive,
    height,
}) => {
    const getSelectedValues = () =>
        items.filter(el => el.checked).map(el => {
            if (el.label != null && typeof el.label === "object")
                return el.value.toString();
            return el.label.toString();
        });

    const change = (option, index, isChecked) => {
        Object.assign(option, { checked: isChecked });
        Object.assign(items[index], { checked: isChecked });
        if (onChange) onChange(option, items);
        if (onActive) onActive(getSelectedValues().length > 0);
    };

    const isActive = () => !!getSelectedValues().length && !noModal;

    const disabled = getSelectedValues().length <= 0;

    const cleanFilterOnClose = () => {
        if (!filter.applied) {
            const newItems = items
                .filter(el => el.checked)
                .map(el => {
                    el.checked = false;
                    return el;
                });
            if (onChange) onChange(items, newItems);
            if (onActive) onActive(false);
        }
    };

    return (
        <Filter
            sufix={sufix}
            type="list"
            isMobile={isMobile}
            className={className}
            noModal={noModal}
            label={label}
            height={height}
            items={items.map((option, index) => {
                return (
                    <Checkbox
                        key={option}
                        disabled={option.disabled}
                        checked={option.checked}
                        onChange={isChecked => change(option, index, isChecked)}
                        label={option.label}
                    />
                );
            })}
            onOpen={() => onOpen()}
            isOpen={open}
            onApply={() => onApply()}
            onReset={() => onReset()}
            onClose={() => { cleanFilterOnClose(); onClose(); }}
            isActive={isActive() && !open}
            value={getSelectedValues().length ? getSelectedValues() : [""]}
            disabled={disabled}
        />
    );
};

FilterOption.defaultProps = {
    open: false,
    className: undefined,
    isMobile: false,
    sufix: "",
    items: [],
    noModal: false,
    onOpen: () => { },
    onApply: () => { },
    onReset: () => { },
    onChange: () => { },
};

FilterOption.propTypes = {
    open: PropTypes.bool,
    className: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
    items: PropTypes.arrayOf(PropTypes.object),
    isMobile: PropTypes.bool,
    sufix: PropTypes.string,
    noModal: PropTypes.bool,
    label: PropTypes.string.isRequired,
    onOpen: PropTypes.func,
    onApply: PropTypes.func,
    onReset: PropTypes.func,
    onChange: PropTypes.func,
};

export default FilterOption;
