export const styles = theme => ({
    root: {
        border: "2px solid transparent",
        cursor: "pointer",
        display: "flex",
        overflow: "hidden",
        padding: theme.spacing(2),
    },
    content: {
        display: "flex",
        flexDirection: "column",
        width: "40%",
    },
    imgContainer: {
        height: "48px",
        overflow: "hidden",
        position: "relative",
        width: "48px",
    },
    thumb: {
        display: "block",
        left: "50%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        top: "50%",
    },
    title: {
        fontWeight: "normal",
        lineHeight: 0,
        margin: 0,
        "& + &": { marginTop: theme.spacing(0.5) },
    },
    detailBtn: {
        color: theme.palette.primary.main,
        height: "auto",
        minWidth: 0,
        padding: 0,
    },
    infoContainer: {
        display: "block",
        margin: `${theme.spacing(2)}px 0`,
    },
    footer: {
        display: "flex",
        flexDirection: "column",
        width: "60%",
    },
    footerITem: {
        border: `1px solid ${theme.palette.grey[50]}`,
        borderRadius: "16px",
        display: "flex",
        padding: theme.spacing(2),
        "& + &": { marginTop: theme.spacing(2) },
    },
    info: {
        display: "flex",
        flex: 1,
        flexDirection: "column",
    },
    infoHeader: { display: "flex" },
    infoTitle: { marginRight: theme.spacing(1) },
    price: {
        borderLeft: `1px solid ${theme.palette.grey[50]}`,
        display: "flex",
        flex: 1,
        flexDirection: "column",
        marginLeft: theme.spacing(2),
        paddingLeft: theme.spacing(2),
    },
    about: {
        margin: `${theme.spacing(2)}px 0`,
        maxWidth: "340px",
    },
    aboutText: {
        display: "block",
        marginTop: theme.spacing(2),
    },
});
