import React from "react";
import PropTypes from "prop-types";
import Grid from "@material-ui/core/Grid";
import Slider from "react-slick";
import withStyles from "@material-ui/core/styles/withStyles";
import Typography from "../Typography";
import Box from "../Box";
import Logo from "../Logo";
import Container from "../Container";
import "slick-carousel/slick/slick.css";
import { styles } from "./styles";
const imgSettings = "e_trim/c_fill,g_auto/e_sharpen/q_auto:eco,f_auto,fl_lossy/e_gradient_fade,y_-0.5,b_black";
const BG = `https://res.cloudinary.com/cvc-corp/image/upload/${imgSettings}/v1583162264/SUB/dokggtkrl88f0jgpxl75.jpg`;

const uuidv4 = require("uuid/v4");

const ContainerSeo = ({ isMobile, content }) => {
    if (isMobile) {
        return (
            <Grid xs={12}>
                <Slider infinite={false} speed={500} slidesToShow={1.25} touchThreshold={100} arrows={false}>
                    {content.map(i => i)}
                </Slider>
            </Grid>
        );
    }
    return content.map(i => (
        <Grid item lg={3} key={uuidv4()}>
            {i}
        </Grid>
    ));
};

const SeoText = ({ classes, content, title, subTitle, isMobile, bg }) => {
    return (
        <Box className={classes.root} style={isMobile ? {} : { background: `url(${bg}) no-repeat center bottom` }}>
            <Container className={classes.container}>
                <Grid container spacing={2} className={classes.grid}>
                    <Grid item xs={12} lg={3}>
                        <Logo type="light" className={classes.logo} />
                        <Typography type={isMobile ? "headline5" : "headline3"} className={classes.title}>
                            {title}
                        </Typography>
                        <Typography type={isMobile ? "body1regular" : "subtitle"} className={classes.subtitle}>
                            {subTitle}
                        </Typography>
                    </Grid>
                    <ContainerSeo isMobile={isMobile} content={content} />
                </Grid>
            </Container>
        </Box>
    );
};

SeoText.defaultProps = {
    isMobile: false,
    bg: ""
};

SeoText.propTypes = {
    classes: PropTypes.object.isRequired,
    content: PropTypes.array.isRequired,
    title: PropTypes.oneOfType([PropTypes.string, PropTypes.object]).isRequired,
    subTitle: PropTypes.string.isRequired,
    isMobile: PropTypes.bool,
    bg: PropTypes.string
};

SeoText.defaultProps = {
    bg: BG
};

export default withStyles(styles, { withTheme: true })(SeoText);
