import React from "react";
import {
    Wifi,
    Parking,
    Bar,
    AirConditioner,
    Reception24hs,
    Pool,
    Kids,
    Restaurant,
    Gym,
} from "@cvccorp-components/icons";
import { Paper, Divider } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles , MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import Stars from "../../Stars/Stars"
import themes from "../../Theme/themes";
import { BrandType } from "../../types/brand";
import FilterMore from "../FilterMore";

const useStyles = makeStyles((theme) => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

type FilterMoreStoryProps = {
    mode: string,
    brand: BrandType,
    label: string,
    isOpen: boolean
}

const filters = [
    {
        type: "range",
        label: "Valor Diárias",
        field: "",
        maskOptions: {
            prefix: "R$ ",
            suffix: "",
            includeThousandsSeparator: false,
            thousandsSeparatorSymbol: ",",
            allowDecimal: true,
            decimalSymbol: ".",
            decimalLimit: 2,
            integerLimit: 7,
            allowNegative: false,
            allowLeadingZeroes: false,
        }
    },
    {
        type: "options",
        label: "Categoria",
        field: "award",
        operator: "and",
        options: [
            { value: 1, checked: false, label: <Stars stars={1} /> },
            { value: 2, checked: false, label: <Stars stars={2} /> },
            { value: 3, checked: false, label: <Stars stars={3} /> },
            { value: 4, checked: false, label: <Stars stars={4} /> },
            { value: 5, checked: false, label: <Stars stars={5} /> },
        ]
    },
    {
        type: "grid",
        label: "Principais Comodidades",
        field: "",
        operator: "or", 
        options: [
            { code: 1, label: "Café da Manhã", icon: Gym, size: "medium", checked: false },
            { code: 2, label: "Restaurante", icon: Restaurant, size: "medium", checked: false },
            { code: 3, label: "Ideal para crianças", icon: Kids, size: "medium", checked: false },
            { code: 4, label: "Piscina", icon: Pool, size: "medium", checked: false },
            { code: 5, label: "Recepção 24hs", icon: Reception24hs, size: "medium", checked: false },
            { code: 6, label: "Ar condicionado", icon: AirConditioner, size: "medium", checked: false },
            { code: 7, label: "Estacionamento", icon: Parking, size: "medium", checked: false },
            { code: 8, label: "Wi-fi grátis", icon: Wifi, size: "medium", checked: false },
            { code: 9, label: "Bar", icon: Bar, size: "medium", checked: false }
        ]
    },
]

const FilterMoreStory = ({
    mode, brand, label, isOpen
}: FilterMoreStoryProps) => {
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="FilterMore">
                <Typography variant="h3">FilterMore</Typography>
                <br />
                <Divider />
                <br />
                <Grid item xs={4}>
                    <FilterMore 
                        label={label}
                        filters={filters}
                        item={{ isActive: false }}
                        isOpen={isOpen}
                    />
                </Grid>
                <div style={{padding: 150}}> </div>
            </Paper>
        </MuiThemeProvider>

    );
};

const argsControls = {
    args: {
        brand: "CVC",
        mode: "root",
        label: "Mais Filtros",
        isOpen: false,
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        },
        label: { type: "string" },
        isOpen: { type: "boolean" }
    }
}

storiesOf("Components/Filtros/FilterMore", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <FilterMoreStory {...args}/>, argsControls);
