import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Typography from "../Typography";
import { styles } from "./styles";

const Price = ({ classes, className, partialPrice, oldPartialPrice, product, totalDays, totalPeople, totalPrice, symbol }) => {
    return (
        <div className={className}>
            {oldPartialPrice && (
                <Typography type="captionregular" className={`${classes.oldPrice} price_${product}`}>
                    {symbol ? `${symbol} R$` : "R$"}
                    {oldPartialPrice}
                    {product === "hotel" ? " / diária" : " / adulto"}
                </Typography>
            )}
            <div>
                <Typography type="body1semibold">{symbol ? `${symbol} R$` : "R$"}</Typography>
                <Typography type="headline3" className="aut-price-parcial">
                    {partialPrice}
                </Typography>
                <Typography type="captionregular">{product === "hotel" ? " / diária" : " / adulto"}</Typography>
            </div>
            <Typography type="captionregular" className="aut-total-days">
                {product === "hotel" ? `${totalDays} diárias` : `${totalPeople} ${product === "package" ? "viajante" : "pessoa"}${totalPeople > 1 ? "s" : ""}`}
            </Typography>{" "}
            <Typography type="captionsemibold" className={`${classes[product]} aut-price-total`}>
                {symbol ? `${symbol} R$` : "R$"}
                {totalPrice}
            </Typography>
        </div>
    );
};

Price.defaultProps = {
    product: "",
    totalPrice: "",
    oldPartialPrice: "",
    partialPrice: "",
    totalPeople: "",
    totalDays: "",
    className: ""
};

Price.propTypes = {
    classes: PropTypes.object.isRequired,
    product: PropTypes.string.isRequired,
    totalPrice: PropTypes.string.isRequired,
    oldPartialPrice: PropTypes.string,
    partialPrice: PropTypes.string.isRequired,
    totalPeople: PropTypes.string,
    totalDays: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    // totalPeople: requiredIf(React.PropTypes.string, props => props.product === 'package'),
    // totalDays: requiredIf(React.PropTypes.string, props => props.product === 'hotel'),
    className: PropTypes.string
};

export default withStyles(styles, { withTheme: true })(Price);
