import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import {
    Plus,
    Minus,
    User,
    Delete,
    ArrowLeft,
} from "@cvccorp-components/icons";

//  COMPONENTS
import Select from "../../Select";
import TextField from "../../TextField";
import Typography from "../../Typography";
import Button from "../../Button";
import Box from "../../Box";
import FilterChip from "../../FilterChip";
import Checkbox from "../../Checkbox";
import Overlay from "../../Overlay";

//  STYLES
import { styles } from "../styles";

//  OPTIONS
import { childAgeOptions } from "./options";

const PaxPackage = ({
    classes,
    error,
    errorMsg,
    open,
    fields,
    onClick,
    handleChange,
    handleAdd,
    handleRemove,
    handleAdults,
    handleRemoveRoom,
    handleChildAge,
    handleRemoveChildren,
    handleAddChildren,
    handleDirectFlight,
    handleClasses,
    handleClear,
    initialState,
    isMobile,
    disabledEco,
    disabledExe,
    height,
}) => {
    const guests = fields.input
        .map(field => field.adults + field.children)
        .reduce((accumulator, item) => accumulator + item, 0);

    const countRoom =
    fields.input.length > 1
        ? `${fields.input.length} Quartos`
        : `${fields.input.length} Quarto`;
    const countGuests = guests > 1 ? `${guests} Viajantes` : `${guests} Viajante`;
    const guestsTextValue = `${countRoom}  -  ${countGuests}`;

    return (
        <>
            <TextField
                label={guestsTextValue}
                icon={<User />}
                size="large"
                transparent
                disabled
                error={error}
                errorMsg={errorMsg}
                onClick={onClick}
                className={classes.customTextPax}
                inputClassName={classes.input}
                labelClassName={classes.input}
                noBorder
            />
            {open && <Overlay onClick={onClick} />}
            <Box className={`${classes.root} ${!open && classes.closed}`}>
                <div className={classes.container}>
                    {isMobile ? (
                        <h1 className={classes.title} onClick={onClick}>
                            <Typography type="body1regular">
                                <ArrowLeft className={classes.icon} size="medium" />
                Selecione os viajantes
                            </Typography>
                        </h1>
                    ) : (
                        <h1 className={classes.title}>
                            <Typography type="body1regular">
                                <User className={classes.icon} size="medium" />
                                {guestsTextValue}
                            </Typography>
                        </h1>
                    )}
                    <div className={classes.spaceButton}>
                        <ul
                            className={clsx(classes.list, classes.section)}
                            style={{ maxHeight: height }}
                        >
                            <li className={classes.listItem}>
                                <span className={classes.content}>
                                    <Typography type="headline5">Quartos</Typography>
                                    <Typography type="body2" className={classes.subtitle}>
                    adicione os quartos
                                    </Typography>
                                </span>
                                <div className={classes.btns}>
                                    <Button
                                        color="primary"
                                        icon={<Minus />}
                                        size="small"
                                        addRemove
                                        variant="outlined"
                                        iconPosition="center"
                                        disabled={fields.input.length <= 1}
                                        onClick={() => handleRemoveRoom()}
                                    />
                                    <Typography type="subtitle" className={classes.btnLabel}>
                                        {fields.input.length}
                                    </Typography>
                                    <Button
                                        color="primary"
                                        icon={<Plus />}
                                        size="small"
                                        addRemove
                                        variant="outlined"
                                        iconPosition="center"
                                        onClick={() => handleAdd()}
                                    />
                                </div>
                            </li>
                            {fields.input.map((field, fieldIdx) => (
                                <li key={`${field}-${fieldIdx}`} className={classes.listItem}>
                                    <ul className={classes.list}>
                                        <h2 className={clsx(classes.listTitle, `PaxPackage-room-index-${fieldIdx}`)}>
                                            <Typography
                                                type="captionsemibold"
                                                // className={classes.subtitle}
                                            >
                        Quarto {fieldIdx + 1}
                                            </Typography>
                                            {fieldIdx > 0 && (
                                                <button
                                                    className={classes.removeRoom}
                                                    onClick={() => handleRemove(fieldIdx)}
                                                >
                                                    <Typography
                                                        type="captionsemibold"
                                                        className={classes.removeRoomText}
                                                    >
                                                        <Delete /> Remover
                                                    </Typography>
                                                </button>
                                            )}
                                        </h2>
                                        <li className={classes.listItem}>
                                            <span className={classes.content}>
                                                <Typography type="headline5">Adultos</Typography>
                                                <Typography type="body2" className={classes.subtitle}>
                          acima de 18 anos
                                                </Typography>
                                            </span>
                                            <div className={classes.btns}>
                                                <Button
                                                    color="primary"
                                                    icon={<Minus />}
                                                    size="small"
                                                    addRemove
                                                    variant="outlined"
                                                    iconPosition="center"
                                                    disabled={field.adults <= 1}
                                                    onClick={() =>
                                                        handleAdults(fieldIdx, field.adults - 1)
                                                    }
                                                />
                                                <Typography type="body" className={classes.btnLabel}>
                                                    {field.adults}
                                                </Typography>
                                                <Button
                                                    color="primary"
                                                    icon={<Plus />}
                                                    size="small"
                                                    addRemove
                                                    variant="outlined"
                                                    iconPosition="center"
                                                    onClick={() =>
                                                        handleAdults(fieldIdx, field.adults + 1)
                                                    }
                                                />
                                            </div>
                                        </li>
                                        <li className={classes.listItem}>
                                            <span className={classes.content}>
                                                <Typography type="headline5">Crianças</Typography>
                                                <Typography type="body2" className={classes.subtitle}>
                          de 0 até 17 anos
                                                </Typography>
                                            </span>
                                            <div className={classes.btns}>
                                                <Button
                                                    color="primary"
                                                    icon={<Minus />}
                                                    size="small"
                                                    addRemove
                                                    variant="outlined"
                                                    iconPosition="center"
                                                    disabled={field.children <= 0}
                                                    onClick={() =>
                                                        handleRemoveChildren(fieldIdx, field.children - 1)
                                                    }
                                                />
                                                <Typography
                                                    type="subtitle"
                                                    className={classes.btnLabel}
                                                >
                                                    {field.children}
                                                </Typography>
                                                <Button
                                                    color="primary"
                                                    icon={<Plus />}
                                                    size="small"
                                                    addRemove
                                                    variant="outlined"
                                                    iconPosition="center"
                                                    onClick={() =>
                                                        handleAddChildren(fieldIdx, field.children + 1)
                                                    }
                                                />
                                            </div>
                                        </li>
                                        {field.children > 0 && (
                                            <li className={classes.liContainer}>
                                                {field.ages.map((age, ageIdx) => (
                                                    <div
                                                        key={`${age}-${ageIdx}`}
                                                        className={classes.body}
                                                    >
                                                        <span className={classes.content}>
                                                            <Typography type="headline5">
                                                                {`Idade da criança ${ageIdx + 1}`}
                                                            </Typography>
                                                            <Typography type="body2regular">
                                ao voltar da viagem
                                                            </Typography>
                                                        </span>
                                                        <span className={classes.ageContainer}>
                                                            <Select
                                                                className={classes.ageSelect}
                                                                size="small"
                                                                label="Idade"
                                                                placeholder="Idade"
                                                                select={childAgeOptions}
                                                                onChange={e =>
                                                                    handleChildAge(fieldIdx, ageIdx, e)
                                                                }
                                                                value={String(age.value)}
                                                            />
                                                        </span>
                                                    </div>
                                                ))}
                                            </li>
                                        )}
                                    </ul>
                                </li>
                            ))}
                            <div className={classes.optionsFlight}>
                                <h2 className={classes.listTitle}>
                                    <Typography type="captionsemibold">Classe</Typography>
                                </h2>
                                <FilterChip
                                    className={classes.flightClass}
                                    label="Classe econômica"
                                    onClick={() => handleClasses("eco")}
                                    isActive={fields.classes === "eco"}
                                    disabled={disabledEco}
                                    size="small"
                                />
                                <FilterChip
                                    className={classes.flightClass}
                                    label="Classe executiva"
                                    onClick={() => handleClasses("exe")}
                                    isActive={fields.classes === "exe"}
                                    disabled={disabledExe}
                                />
                                <Checkbox
                                    label="Somente voo direto"
                                    value={fields.directFlight}
                                    onChange={handleDirectFlight}
                                    checked={fields.directFlight}
                                />
                            </div>
                        </ul>
                        {isMobile ? (
                            <div
                                className={
                                    open
                                        ? classes.footer
                                        : clsx(classes.footer, classes.footerClose)
                                }
                            >
                                <Button
                                    color="secondary"
                                    label={`Aplicar ${guestsTextValue}`}
                                    variant="contained"
                                    onClick={() => handleChange(guestsTextValue)}
                                    fullWidth
                                />
                            </div>
                        ) : (
                            <div className={classes.footer}>
                                <Button
                                    className={classes.btn}
                                    color="primary"
                                    label="Limpar"
                                    variant="text"
                                    disabled={
                                        JSON.stringify(initialState) === JSON.stringify(fields)
                                    }
                                    onClick={handleClear}
                                />
                                <Button
                                    className={classes.btnApply}
                                    color="primary"
                                    label="Aplicar"
                                    variant="text"
                                    onClick={() => handleChange(guestsTextValue)}
                                />
                            </div>
                        )}
                    </div>
                </div>
            </Box>
        </>
    );
};

PaxPackage.defaultProps = {
    height: "auto",
};

PaxPackage.propTypes = {
    classes: PropTypes.object.isRequired,
    rooms: PropTypes.array,
    height: PropTypes.string,
};

export default withStyles(styles, { withTheme: true })(PaxPackage);
