import React from "react";
import { Paper, Divider, } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles , MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import CIA from "../../assets/img/cias/latam.svg";
import themes from "../../Theme/themes";
import { BrandType } from "../../types/brand";
import Matrix from "../Matrix";

const useStyles = makeStyles((theme) => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

type MatrixStoryProps = {
    mode: string,
    brand: BrandType,
    isLoading: boolean,
}

const MatrixStory = ({
    mode, brand, isLoading
}: MatrixStoryProps) => {
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="Matrix">
                <Typography variant="h3">Matrix</Typography>
                <br />
                <Divider />
                <br />
                <Grid>
                    <Matrix
                        isLoading={isLoading}
                        cias={[
                            {
                                name: "Latam",
                                img: CIA,
                                directFlight: {
                                    price: "8.098",
                                    isBestOfCategory: true,
                                    isBestOfGeneral: false,
                                },
                                oneStopFlight: {
                                    price: "6.896",
                                    isBestOfCategory: false,
                                    isBestOfGeneral: false,
                                },
                                moreStopFlight: {
                                    price: "6.836",
                                    isBestOfCategory: true,
                                    isBestOfGeneral: true,
                                },
                            },
                            {
                                name: "Gol",
                                img: CIA,
                                directFlight: {
                                    price: "8.430",
                                    isBestOfCategory: false,
                                    isBestOfGeneral: false,
                                },
                                oneStopFlight: {
                                    price: "6.870",
                                    isBestOfCategory: true,
                                    isBestOfGeneral: false,
                                },
                                moreStopFlight: {
                                    price: "7.200",
                                    isBestOfCategory: false,
                                    isBestOfGeneral: false,
                                },
                            },
                            {
                                name: "Avianca",
                                img: CIA,
                                directFlight: null,
                                oneStopFlight: {
                                    price: "8.028",
                                    isBestOfCategory: false,
                                    isBestOfGeneral: false,
                                },
                                moreStopFlight: {
                                    price: "8.124",
                                    isBestOfCategory: false,
                                    isBestOfGeneral: false,
                                },
                            },
                        ]}
                    />
                </Grid>
            </Paper>
        </MuiThemeProvider>

    );
};

const argsControls = {
    args: {
        brand: "CVC",
        mode: "root",
        isLoading: false,
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        },
        isLoading: {
            type: "boolean"
        },
    }
}

storiesOf("Components/Filtros/Matrix", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <MatrixStory {...args}/>, argsControls);
