import React, { useEffect } from "react";
import { Link } from "@material-ui/core";
import { Auth } from "aws-amplify";
import { injectIntl, IntlShape } from "react-intl";
import Typography from "../../../Typography";
import useGa from "../../../utils/useGa";
import { useAuthAmplifyContext } from "../../contexts/AmplifyContext";
import { useRenderSteps } from "../../contexts/renderSteps";
import HeaderLogin from "../../shared/components/HeaderLogin";
import InternalButton from "../../shared/components/InternalButton";
import SubTitle from "../../shared/components/SubTitle";
import Terms from "../../shared/components/Terms";
import Title from "../../shared/components/Title";
import { useUserData } from "../../shared/hooks/UserContext";
import translate from "../../shared/i18n/translate";
import useStyles from "./styles";
interface ICodeNonReceived {
    intl: IntlShape;
    onCloseClick?(): void;
    isModal?: boolean;
}

const CodeNonReceived: React.FC<ICodeNonReceived> = ({ intl, onCloseClick, isModal }) => {
    const classes = useStyles({});
    const { renderNewStep, setPreviousStep } = useRenderSteps();
    const { authAmplify } = useAuthAmplifyContext();
    const { saveOnGa } = useGa();
    const { user, setUser } = useUserData();

    useEffect(() => {
        setPreviousStep("ValidationCode");

    }, []);

    const handleSendCode = () => new Promise(async () => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Validation code",
            label: "action :: forwarding recovery code"
        });

        try {
            if (authAmplify === "confirmSignUp") {
                await Auth.resendSignUp(user.email);
                renderNewStep("ValidationCode");
            } else {
                await Auth.forgotPassword(user.email);
                renderNewStep("ValidationCode");
            }
        } catch (err) {
            // exibir erro servidor
        }
    });
    const handleMainScreen = () => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Change email",
            label: "action :: change email"
        });
        setUser({
            ...user,
            email: ""
        });
        renderNewStep("MainScreen");
    };
    return (
        <>
            <HeaderLogin isModal={isModal} previousStep="ValidationCode" onCloseClick={onCloseClick} />
            <div className={classes.root}>
                <Title>
                    {translate({ id: "CodeNonReceived.title" })}
                </Title>
                <Typography type="body2regular" className={classes.subTitle}>
                    {translate({
                        id: "CodeNonReceived.subTitle",
                        value: {
                            email: <strong>{user ? user.email : ""}</strong>,
                        },
                    })}
                </Typography>
                <Typography type="body2regular" className={classes.cautionText}>
                    {translate({ id: "CodeNonReceived.caution" })}
                </Typography>
                <Link onClick={(handleMainScreen)} className={classes.changeEmail}>
                    <SubTitle variant="caption" className={classes.subTitleLinkChangeEmail}>
                        {translate({ id: "CodeNonReceived.linkChangeEmail" })}
                    </SubTitle>
                </Link>
                <div className={classes.buttonContainer}>
                    <InternalButton
                        text={intl.formatMessage({ id: "CodeNonReceived.actionButton" })}
                        handlerClick={handleSendCode}
                    />
                </div>

            </div>
            <Terms />
        </>
    );
};

export default injectIntl(CodeNonReceived);
