import clsx from "clsx";
import React, { useEffect, useState } from "react";
import FilterRange from "../FilterRange/FilterRange";

const _FilterRange = prop => {
    const {
        filters,
        filter,
        index,
        onApply,
        noModal,
        onChange,
        onActive,
        className,
        height,
    } = prop;

    const [item, setItem] = useState({ ...filter });

    const _onOpen = () => {
        setItem(item => ({ ...item, open: true }));
    };

    const _handleBlur = field => {
        setItem(item => {
            const _item = { ...item };
            let change = false;
            const from = Number(_item.rangeTextValues[field]);
            const min = Number(_item.min);
            const max = Number(_item.max);

            if (field === "min") change = !(from >= min && from <= max);
            if (field === "max") change = !(from <= max && from >= min);

            if (from === 0 && _item.rangeValue[field] !== _item[field]) {
                _item.rangeTextValues[field] = _item.rangeValue[field];
            } else {
                if (change) {
                    _item.rangeTextValues[field] = _item[field];
                    _item.rangeValue[field] = _item[field];
                }

                if (_item.rangeTextValues.max < _item.rangeTextValues.min && !change) {
                    change = true;
                    const range = { ..._item.rangeTextValues };
                    _item.rangeTextValues.min = range.max;
                    _item.rangeTextValues.max = range.min;
                    _item.rangeValue.min = range.max;
                    _item.rangeValue.max = range.min;
                }
            }

            if (!change) _item.rangeValue[field] = from;

            return _item;
        });
    };

    const _onChange = vls => {
        setItem(item => ({ ...item, rangeValue: vls, rangeTextValues: vls }));
    };

    const _onChangeCommitted = vls => {
        setItem(item => {
            const _item = {
                ...item,
                rangeValue: vls,
                rangeTextValues: vls,
                isActive: JSON.stringify({ min: item.min, max: item.max }) !==
                    JSON.stringify(vls)
            };
            if (onChange) onChange(_item);
            if (onActive)
                onActive(_item.isActive);

            return _item;
        });
    };

    const _handleFocus = field => {
        setItem(item => ({
            ...item,
            rangeTextValues:
                field === "min"
                    ? { min: "", max: item.rangeValue.max }
                    : { min: item.rangeValue.min, max: "" },
        }));
    };

    const _handleChange = (value, field) => {
        const replaceValue = value.replace(item.maskOptions.prefix, "").replace(".", "");
        if (Number(replaceValue) || replaceValue === 0 || replaceValue === "") {
            const newRange = {
                min: field === "min" ? Number(replaceValue) : item.rangeTextValues.min,
                max: field === "max" ? Number(replaceValue) : item.rangeTextValues.max
            };
            setItem(item => {
                const _item = {
                    ...item,
                    rangeValue: newRange,
                    rangeTextValues: newRange,
                    isActive: JSON.stringify({ min: item.min, max: item.max }) !==
                    JSON.stringify(newRange)
                };
                if (onChange) onChange(_item);
                if (onActive)
                    onActive(_item.isActive);

                return _item;
            });
        }
    };

    const _onApply = v => {
        setItem(item => {
            const _item = { ...item, open: false, filterTitle: v };
            filters[index] = _item;
            onApply(filters);
            return _item;
        });
    };

    const _onReset = () => {
        setItem(item => {
            const _item = { ...filter };

            _item.open = item.open;
            _item.rangeTextValues = { min: _item.min, max: _item.max };
            _item.rangeTitleValues = { min: _item.min, max: _item.max };
            _item.rangeValue = { min: _item.min, max: _item.max };
            _item.filterTitle = "";

            filters[index] = _item;
            onApply(filters);
            return _item;
        });
    };

    const cleanFilterOnClose = () => {
        if (!item.filterTitle) {
            setItem(item => {
                const _item = {
                    ...item,
                    rangeValue: filter.rangeValue,
                    rangeTextValues: filter.rangeTextValues,
                    isActive: false
                };
                if (onChange) onChange(_item);
                if (onActive) onActive(_item.isActive);

                return _item;
            });
        }
    };

    const _onClose = () => {
        cleanFilterOnClose();
        setItem(item => ({ ...item, open: false }));
    };

    return (
        <FilterRange
            filterTitle={item.filterTitle}
            onOpen={_onOpen}
            onClose={_onClose}
            onChange={_onChange}
            onChangeCommitted={_onChangeCommitted}
            handleFocus={_handleFocus}
            handleBlur={_handleBlur}
            handleChange={_handleChange}
            onApply={_onApply}
            onReset={_onReset}
            label={item.label}
            rangeTitleValues={item.rangeTitleValues}
            rangeTextValues={item.rangeTextValues}
            rangeValue={item.rangeValue}
            maskOptions={item.maskOptions}
            step={10}
            open={item.open}
            noModal={noModal}
            values={{ min: item.min, max: item.max }}
            className={clsx(className, `FilterRange-item-${index}`)}
            height={height}
        />
    );
};

export default _FilterRange;
