import React, { useState } from "react";
import Dropdown from "@cvccorp-components/icons/dist/DropDown";
import withStyles from "@material-ui/core/styles/withStyles";
import clsx from "clsx";
import PropTypes from "prop-types";
import Box from "../Box";
import Overlay from "../Overlay";
import Typography from "../Typography";
import { styles } from "./styles";

const Modal = ({ classes, onOpen, onClose, isOpen, title, children, isMobile = false }) => {
    const [active, setActive] = useState(isOpen);
    const classDropdown = (() => {
        if(isMobile) return active ? classes.icon : clsx(classes.icon, classes.iconActive)
        return active ? clsx(classes.icon, classes.iconActive) : classes.icon
    })();

    const handleClose = () => {
        setActive(false);
        if (onClose) onClose();
    };

    const handleOpen = () => {
        if (!active) {
            setActive(true);
            if (onOpen) onOpen();
        }
        else {
            setActive(false);
        }
    };

    return (
        <Box
            onClick={handleOpen}
            className={
                active
                    ? clsx(classes.opened, classes.root)
                    : clsx(classes.closed, classes.root)
            }
        >
            {active && <Overlay className={classes.overlay} onClick={handleClose} />}
            <header className={classes.header}>
                <Typography type="body2semibold">{title}</Typography>
                <Dropdown className={classDropdown} />
            </header>
            <div className={active ? classes.openedContent : classes.closedContent}>
                {children}
            </div>
        </Box>
    );
};

Modal.propTypes = {
    classes: PropTypes.object.isRequired,
    onOpen: PropTypes.func,
    onClose: PropTypes.func,
    isOpen: PropTypes.bool,
    title: PropTypes.string,
    children: PropTypes.object,
};

export default withStyles(styles, { withTheme: true })(Modal);
