    discount,
    partialPrice,
    totalPeople,
    attribute,
    taxesIncluded,
    parcel,
    parcelInfo,
    isFavorite,
    isActive,
    id,
    label,
    more,
    link,
    onDetail,
    onViewMore
}) => {
    const NextArrow = props => {
        const handleClick = e => {
            if (props.onClick) props.onClick(e);
            e.stopPropagation();
        };
        return (
            <button type="button" className={clsx(classes.arrows, classes.arrowRight)} onClick={e => handleClick(e)}>
                <DropRight size="medium" />
            </button>
        );
    };

    const PrevArrow = props => {
        const handleClick = e => {
            if (props.onClick) props.onClick(e);
            e.stopPropagation();
        };
        return (
            <button type="button" className={clsx(classes.arrows, classes.arrowLeft)} onClick={e => handleClick(e)}>
                <DropLeft size="medium" />
            </button>
        );
    };

    const classNameBox = () => {
        if (isLoading) {
            return clsx(classes.root, classes.rootLoading, className);
        }
        return clsx(classes.root, classes.rootList, className, isActive && classes.isActive);
    };

    const totalAvaliation = () => {
        const aval = totalAvaliations.slice(0, 3);
        return (
            <div>
                <Typography type="body2regular">{`${aval.join("-")}`}</Typography>
            </div>
        );
    };

    const renderAvaliations = () => {
        const aval = totalAvaliations.slice(0, 3);

        return (
            <div className={classes.trip}>
                <DocumentText noStroke size="medium" />
                <div className={classes.attributesContent}>
                    <div className={classes.rate}>
                        <Typography type="body1semibold">{attribute}</Typography>
                    </div>
                    <div className={classes.totalAvaliation}>
                        <Typography type="body2regular">{`${aval.join("-")}`}</Typography>
                    </div>
                    {totalAvaliations.length > 3 && <Button className={classes.viewMore} label={more} variant="text" onClick={() => onViewMore()} />}
                </div>
            </div>
        );
    };

    const renderSkeleton = () => (
        <Grid container spacing={2} className={classes.loading}>
            <Grid item xs={6} className={classes.relative}>
                <div className={classes.imgContainerPromoSmall}>
                    <LoadingContent absolute />
                </div>
            </Grid>
            <Grid item xs={6}>
                <Grid container>
                    <Grid item xs={10}>
                        <LoadingContent />
                    </Grid>
                </Grid>
                <Grid container className={classes.loadingContent}>
                    <Grid item xs={5}>
                        <LoadingContent />
                    </Grid>
                </Grid>
                <Grid container className={classes.loadingContent}>
                    <Grid item xs={9}>
                        <LoadingContent />
                    </Grid>
                </Grid>
                <Grid container className={classes.loadingPrice}>
                    <Grid item xs={3}>
                        <LoadingContent />
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    );

    const renderImages = () => {
        if (!imgs) {
            return null;
        }
        return (
            <div className={classes.imgContainerList}>
                {imgs.length > 1 && <span className={clsx(classes.overlay, "overlay")} />}
                <Slider infinite={false} speed={500} slidesToShow={1} touchThreshold={100} dots nextArrow={<NextArrow />} prevArrow={<PrevArrow />}>
                    {imgs.map((item, i) => (
                        <span className={clsx(classes.imgWrapper, classes.imgWrapperList)} key={i}>
                            <LazyLoad height={229} placeholder={<LoadingContent />}>
                                <img className={notAvaliable ? clsx(classes.notAvaliableImg, classes.img) : classes.img} src={`https://almundo-com-res.cloudinary.com/image/fetch/${cloudinarySettings}/${item}`} alt="" />
                            </LazyLoad>
                        </span>
                    ))}
                </Slider>
            </div>
        );
    };

    const renderLeftColumn = () => {
        return (
            <div className={classes.contentColumn}>
                <Typography className={classes.title} type="headline6">
                    {title}
                </Typography>
                <Typography className={classes.subtitle} type="body2regular">
                    {stars && <Stars stars={stars} size="small" className={clsx("hotel-stars", classes.stars)} />}
                    {subtitle && (
                        <span>
                            {subtitle} | <strong>{link}</strong>
                        </span>
                    )}
                </Typography>
                {renderAvaliations()}
            </div>
        );
    };

    const renderRightColumn = () => {
        const renderDiscount = () => {
            if (!discount) {
                return null;
            }
            return (
                <div className={classes.infoDiscount}>
                    <Tag className={classes.discountList} label={discount} color="hotel" />
                </div>
            );
        };

        return (
            <div className={classes.contentColumn}>
                {renderDiscount()}
                <div className={classes.priceContainer}>
                    <Typography type="body1semibold">R$</Typography>
                    <Typography type="headline3">{partialPrice}</Typography>
                </div>
                <div className={classes.totalPeople}>
                    <Typography type="captionregular">{`${totalPeople} viajantes adultos`} </Typography>
                </div>

                <div className={classes.btnDetalhe}>
                    <Grid container item xs={12} justify="flex-end">
                        <Button color="primary" label={label} variant="contained" onClick={() => onDetail()} />
                    </Grid>
                </div>
            </div>
        );
    };

    const renderContent = () => {
        const renderFooter = () => {
            if (!taxesIncluded && !parcel && !parcelInfo) {
                return null;
            }
            return (
                <div className={classes.footer}>
                    {taxesIncluded && (
                        <Typography className={classes.footerItem} type="captionregular">
                            Taxas inclusas
                        </Typography>
                    )}
                    {parcel && (
                        <Typography className={classes.footerItem} type="captionsemibold">
                            Em até {parcel}x
                        </Typography>
                    )}
                    {parcelInfo && !parcel && (
                        <Typography className={classes.footerItem} type="captionsemibold">
                            {parcelInfo}
                        </Typography>
                    )}
                </div>
            );
        };

        return (
            <div className={classes.content}>
                {renderLeftColumn()}
                {renderRightColumn()}
                {renderFooter()}
            </div>
        );
    };

    return (
        <Box className={classNameBox()} onClick={onClick} id={id} key={key}>
            {!isLoading && (
                <div className={classes.typeList}>
                    {renderImages()}
                    {notAvaliable && <Tag className={imgs ? classes.topTag : ""} label="Indisponível no momento" color="white" />}
                    {onFav && (
                        <span className={clsx(classes.favorite, classes.favoriteList)}>
                            <Favorite fill={isFavorite} size="medium" onClick={onFav} />
                        </span>
                    )}
                    {renderContent()}
                </div>
            )}
            {isLoading && renderSkeleton()}
        </Box>
    );
};

TourItem.defaultProps = {
    imgs: [],
    isLoading: false,
    notAvaliable: false,
    isFavorite: false,
    taxesIncluded: false,
    onFav: undefined,
    cloudinarySettings: "",
    totalAvaliations: [],
    onClick: () => {},
    totalPeople: "2"
};
TourItem.prototype = {
    key: PropTypes.string,
    imgs: PropTypes.array,
    isLoading: PropTypes.bool,
    onClick: PropTypes.func,
    onDetail: PropTypes.func,
    onViewMore: PropTypes.func,
    title: PropTypes.string,
    subtitle: PropTypes.string,
    cloudinarySettings: PropTypes.string,
    notAvaliable: PropTypes.bool,
    isFavorite: PropTypes.bool,
    onFav: PropTypes.func,
    stars: PropTypes.number,
    partialPrice: PropTypes.string,
    amenities: PropTypes.array,
    taxesIncluded: PropTypes.bool,
    parcel: PropTypes.string,
    parcelInfo: PropTypes.string,
    totalAvaliations: PropTypes.array,
    discount: PropTypes.string,
    attribute: PropTypes.string,
    label: PropTypes.string,
    more: PropTypes.string,
    href: PropTypes.string.isRequired,
    target: PropTypes.string,
    link: PropTypes.string.isRequired
};

export default withStyles(styles, { withTheme: true })(TourItem);
