export const styles = theme => ({
    container: {
        background: theme.palette.white.main,
        [theme.breakpoints.between("md", "xl")]: {
            borderRadius: "8px",
            boxShadow: theme.shadows[8]
        },
        cursor: "default"
    },
	containerDates: {
		position: "relative",
		display: "flex",
	},
    calendar: {
        overflow: "hidden",
        transition: "height ease-in-out .5s",
        zIndex: theme.level.modal,
        [theme.breakpoints.between("xs", "md")]: {
            background: theme.palette.white.main,
            bottom: 0,
            left: 0,
            height: "100%",
            overflow: "auto",
            position: "fixed",
            right: 0,
            top: 0
        },
        [theme.breakpoints.between("md", "xl")]: {
            height: "480px",
            left: "-11px",
            padding: "0 20px",
            position: "absolute",
            top: "9px"
        }
    },
    closed: { height: 0 },
    header: {
        display: "flex",
        justifyContent: "space-between",
        padding: theme.spacing(2, 0, 1, 0),
        margin: theme.spacing(0, 2, 1, 2),
        borderBottom: "1px solid #DDDDDD",
        [theme.breakpoints.between("xs", "md")]: {
            flexDirection: "column",
            cursor: "default"
        },
        [theme.breakpoints.between("md", "xl")]: { alignItems: "center" }
    },
    headerMob: {
        display: "flex",
        justifyContent: "space-between",
        [theme.breakpoints.between("xs", "md")]: {
            flexDirection: "column",
            cursor: "default"
        },
        [theme.breakpoints.between("md", "xl")]: { alignItems: "center" }
    },
    title: {
        color: theme.palette.text.primary,
        fontWeight: "bold",
        [theme.breakpoints.between("xs", "md")]: {
            alignItems: "center",
            display: "flex",
            paddingBottom: theme.spacing(2),
            // position: "relative",
            cursor: "pointer",
            "&:after": {
                borderTop: `1px solid ${theme.palette.grey[50]}`,
                bottom: 0,
                content: "",
                left: theme.spacing(2) * -1,
                position: "absolute",
                right: theme.spacing(2) * -1
            }
        }
    },
    icon: { marginRight: theme.spacing(1) },
    itensHeader: {
        display: "flex",
        flexGrow: 1,
        flexDirection: "row",
        justifyContent: "space-between"
    },
    itensHeaderMob: {
        display: "flex",
        flexGrow: 1,
        flexDirection: "row",
        justifyContent: "space-between",
        borderBottom: "1px solid #DDDDDD",
        padding: theme.spacing(2, 2, 0, 2)
    },
    containerBtn: {
        marginRight: "10px"
    },
	calendarIcon: {
		stroke: "none",
		fill: "currentColor",
		width: "25px",
		color:"#333333",
		marginLeft: "-6px"
	},
    buttonClear: {
		height: "auto",
        marginTop: "auto",
		fontWeight: "700",
        marginBottom: theme.spacing(1),
        minWidth: 0,
        padding: 0,
        backgroundColor: "transparent",
        color: theme.palette.primary.main,
        "&:hover": {
            backgroundColor: "transparent"
        }
    },
    dates: {
        display: "flex",
        boxSizing: "border-box",
        [theme.breakpoints.between("xs", "md")]: {
            marginTop: theme.spacing(2)
        },
        borderRadius: "8px",
        border: "1px solid #999999",
        marginBottom: theme.spacing(1),
        [theme.breakpoints.down("md")]: {
            width: "619px",
            marginLeft: "auto",
            marginRight: "auto"
        },
        [theme.breakpoints.down(767)]: {
            width: "100%"
        }
    },
    datesMob: {
        display: "flex",
        boxSizing: "border-box",
        [theme.breakpoints.between("md", "xl")]: {
            width: "calc(50% - 22px )"
        },
        [theme.breakpoints.down("md")]: {
            width: "619px",
            marginLeft: "auto",
            marginRight: "auto"
        },
        [theme.breakpoints.down(767)]: {
            width: "100%"
        }
    },
    datesItem: {
        flex: 1
    },
    dateInput: { padding: 0 },
    content: {
        [theme.breakpoints.between("xs", "md")]: {
            height: "calc(100%)",
            position: "absolute",
            width: "100%"
        },
        [theme.breakpoints.between("md", "xl")]: {
            width: "619px"
        }
    },
    footer: {
        background: theme.palette.white.main,
        boxShadow: theme.shadows[24],
        bottom: 0,
        opacity: 0,
        padding: theme.spacing(2),
        position: "absolute",
        transition: "opacity ease-in-out 2s",
        width: "100%",
        boxSizing: "border-box"
    },
    showFooter: {
        opacity: 1,
        position: "fixed"
    },
    footerContainer: {
        display: "flex",
        margin: "0 16px 16px 16px",
        alignItems: "center",
        borderTop: "1px solid #DDDDDD",
        paddingTop: "16px",
        paddingBottom: "16px"
    },
    footerDayCounter: {
        display: "flex",
        justifyContent: "flex-start",
        flexGrow: "1",
		color: "#333333 !important"
    },
    footerConfirmButton: {
        display: "flex",
        justifyContent: "flex-end"
    },
    btnApply: {
        height: "auto",
        minWidth: 0,
        borderRadius: "8px"
    },
    input: { fontSize: "15px", borderRadius: 0 },
	inputInside:{ fontSize: "15px", borderRadius: 0 },
    inputMobile: {
        fontSize: "16px"
    },
    inputRight: { 
		borderTopRightRadius: "8px",
		borderBottomRightRadius: " 8px",
		background: "#FFFFFF",
		width: "100%" 
	},
    inputLeft: { 
		borderTopLeftRadius: "8px", 
		borderBottomLeftRadius: " 8px", 
		background: "#FFFFFF",
		width: "100%" 
	},
    rootInput: {
        padding: "0 !important",
    },
    separator: {
        borderRight: "1px solid #DDDDDD"
    },
	dateSeparator: {
		borderRight: "1px solid #DDDDDD",
		marginTop: "8px",
		marginBottom: "8px"
	  },
    inputDate: {
		padding: "0px",
        color: "#333333",
		fontFamily: "HindMBold",
        "&::placeholder": { color: "#333333", fontWeight: "bold" }
    },
    inputRadius: {
        borderRadius: "8px",
		background: "#FFFFFF",
    },
    inputRadiusMobile: {
        borderRadius: "0 !important",
		background: "#FFFFFF",
    },
});
