import React, { useEffect, useState } from "react";
import withStyles from "@material-ui/core/styles/withStyles";

import Warning from "../assets/img/warning.svg";
import Close from "../assets/img/close.svg";
import Seta from "../assets/img/seta.svg";
import { styles } from "./styles";

function setCookie(prefix, value) {
    const date = new Date();
    date.setTime(date.getTime() + EXPIRES_IN_DAY * 24 * 60 * 60 * 1000);
    const expires = "expires=" + date.toUTCString();
    document.cookie = prefix + COOKIE_NAME + "=" + value + ";" + expires + ";path=/";
}

function getCookie(name) {
    const key = name + "=";
    const cookies = document.cookie.split(";");
    for (let i = 0; i < cookies.length; i++) {
        let cookie = cookies[i];
        while (cookie.charAt(0) == " ") {
            cookie = cookie.substring(1);
        }
        if (cookie.indexOf(key) == 0) {
            return cookie.substring(key.length, cookie.length);
        }
    }
    return "";
}

function isCookieExists(prefix) {
    const covid19 = getCookie(prefix + COOKIE_NAME);

    return covid19 === "true";
}

const COOKIE_NAME = "covid19";
const EXPIRES_IN_DAY = 365;

function CovidWrapper({ cookiePrefix = "" }) {
    const [isExists, setIsExists] = useState(isCookieExists(cookiePrefix));

    useEffect(() => {
        setCookie(cookiePrefix, isExists);
    }, [isExists]);

    return <Covid19 isCookieExists={isExists} setCookie={setIsExists} />;
}

const Covid19 = withStyles(styles, { withTheme: true })(({ classes, isCookieExists, setCookie }) => {
    if (isCookieExists) return <></>;

    return (
        <div className={classes.coronaWarning} id="coronaWarning">
            <div className={classes.coronaFlex}>
                <img className="alertSvg" src={Warning} alt="Alert" />
                <p>
                    <span>Importante: </span>
                    <br className="hide-desk" />
                    Entenda como estamos atuando e saiba como proceder com a sua viagem.
                </p>
                <a target="_blank" rel="noreferrer" href="/coronavirus">
                    Acesse <img src={Seta} alt="seta" />
                </a>
            </div>
            <img className={classes.coronaClose} src={Close} alt="Fechar" onClick={() => setCookie(true)} />
        </div>
    );
});

export default CovidWrapper;
