import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Button from "@material-ui/core/Button";
import Typography from "../Typography";
import { styles } from "./styles";

const FilterChip = ({
    classes,
    className,
    matrix,
    isAmenities,
    isLuggage,
    isActive,
    disabled,
    onClick,
    label,
}) => {
    const [active, setActive] = useState(false);

    useEffect(() => {
        setActive(isActive);
    }, [isActive]);

    return (
        <Button
            classes={{
                root: classes.root,
                label: classes.label,
                disabled: classes.disabled,
                outlined: classes.outlined,
            }}
            variant="contained"
            className={`
        ${className}
        ${active ? classes.isActive : ""}
        ${matrix ? classes.matrix : ""}
        ${isAmenities ? classes.isAmenities : ""}
        ${isLuggage && !active ? classes.luggage : ""}
        `}
            disabled={disabled}
            onClick={() => {
                setActive(!active);
                onClick(!active);
            }}
        >
            <Typography
                type="captionregular"
                className={`
        ${classes.rootLabel} 
        ${active && classes.isActiveLabel}
        ${isLuggage && !active && classes.luggageLabel}
        ${disabled && classes.disabledLabel}
        `}
            >
                {label}
            </Typography>
        </Button>
    );
};

FilterChip.propTypes = {
    classes: PropTypes.object.isRequired,
    className: PropTypes.string,
    isActive: PropTypes.bool,
    matrix: PropTypes.bool,
    isAmenities: PropTypes.bool,
    isLuggage: PropTypes.bool,
    disabled: PropTypes.bool,
    onClick: PropTypes.func,
    label: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
};

export default withStyles(styles, { withTheme: true })(FilterChip);
