import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import WithTax from "@cvccorp-components/icons/dist/WithTax";
import WithoutTax from "@cvccorp-components/icons/dist/WithoutTax";
import Hotel from "@cvccorp-components/icons/dist/Hotel";
import Typography from "../../Typography";
import { styles } from "./styles";
import clsx from "clsx";

const Info = ({ classes, className, bedInfo, noRefundable }) => {
    return (
        <Typography className={className} type="body2regular">
            {bedInfo && (
                <span className={classes.info}>
                    <Hotel size="small" /> {bedInfo}
                </span>
            )}
            {noRefundable ? (
                <span className={clsx(classes.info, "RoomInfo-not-refundable")}>
                    <WithoutTax size="small" /> Tarifa não reembolsável{" "}
                </span>
            ) : (
                <span className={clsx(classes.info, "RoomInfo-refundable")}>
                    <WithTax size="small" /> Tarifa reembolsável{" "}
                </span>
            )}
        </Typography>
    );
};

Info.propTypes = {
    classes: PropTypes.object.isRequired,
    className: PropTypes.string,
    bedInfo: PropTypes.string,
    noRefundable: PropTypes.bool,
};

export default withStyles(styles, { withTheme: true })(Info);
