import React from "react";
import { makeStyles, withWidth } from "@material-ui/core";
import { isWidthDown } from "@material-ui/core/withWidth";
import { useContext } from "react";
import { BrandContext } from "../BrandContext";
import { isWebView } from "../WebView";

export const HeaderHideBgContext = React.createContext(false);

const useHeaderBlockStyles = makeStyles({
    root: (prop: any) => ({
        backgroundImage: `url(${prop.img})`,
        backgroundPosition: "center center",
        backgroundSize: "cover"
    })
});
function HeaderBlockWithImg({ width, children }) {
    const ctx = useContext(BrandContext);
    const isMobile = isWidthDown("sm", width);
    const img = isMobile ? ctx.headerImageBg?.mobile : ctx.headerImageBg?.desktop;
    const classes = useHeaderBlockStyles({ img });
    return (
        <div className={classes.root}>
            {children}
        </div>
    );
}
function HeaderBlockDefault({ children }) {
    return (
        <div>
            {children}
        </div>
    );
}
function HeaderBlock({ width, children }) {
    const ctx = useContext(BrandContext);
    const hideHeaderBg = ctx.headerImageBg != null;
    const T = hideHeaderBg ? HeaderBlockWithImg : HeaderBlockDefault;

    if (isWebView)
        return null;
    return (
        <HeaderHideBgContext.Provider value={hideHeaderBg}>
            <T width={width}>
                {children}
            </T>
        </HeaderHideBgContext.Provider>
    )
}
export default withWidth()(HeaderBlock);
