/* eslint-disable @typescript-eslint/ban-ts-comment */
import React, { useMemo, useRef, useContext, useEffect, useState } from "react";
import Link from "@material-ui/core/Link";
import { FormHandles } from "@unform/core";
import { Auth } from "aws-amplify";
import { IntlShape, injectIntl } from "react-intl";
import * as Yup from "yup";
import { BrandContext } from "../../../BrandContext";
import { Redirect } from "../../../BrandContext/amplify/aws.config"
import { useProfileContext } from "../../../ProfileContext";
import { useUserDataContext } from "../../../UserContext";
import { featureToggles, getParam } from "../../../utils";
import useGa from "../../../utils/useGa";
import { useAuthAmplifyContext } from "../../contexts/AmplifyContext";
import { useLoadingContext } from "../../contexts/LoadingContext";
import { useRenderSteps } from "../../contexts/renderSteps";
import { useToast } from "../../contexts/Toast";
import Form from "../../shared/components/Form";
import HeaderLogin from "../../shared/components/HeaderLogin";
import Input from "../../shared/components/Input";
import InternalButton from "../../shared/components/InternalButton";
import Loading from "../../shared/components/Loading";
import SubTitle from "../../shared/components/SubTitle";
import Terms from "../../shared/components/Terms";
import Title from "../../shared/components/Title";
import { useUserData } from "../../shared/hooks/UserContext";
import translate from "../../shared/i18n/translate";
import getValidationErrors from "../../shared/utils/getValidationErrors";
import useStyles from "./styles";

interface ValidationCodeProps {
    code: string;
}
interface TValidationCode {
    intl: IntlShape;
    onCloseClick?(): void;
    isModal?: boolean;
}

const ValidationCode: React.FC<TValidationCode> = ({ intl, onCloseClick, isModal }) => {
    const classes = useStyles({});
    const { renderNewStep, setPreviousStep } = useRenderSteps();
    const { authAmplify, amplifyTempPwd, amplifyPwd } = useAuthAmplifyContext();
    const { user } = useUserData();
    const [hasDoneProfileReq , setHasDoneProfileReq] = useState(false)
    const ctx = useContext(BrandContext);
    const formRef = useRef<FormHandles>(null);
    const { setLoading, loading } = useLoadingContext();
    const { addToast } = useToast();
    const { setUserIdToken } = useUserDataContext();

    const { profileData, getProfileInformations } = useProfileContext()

    const errorWarningExpiredCodeTitle = intl.formatMessage({ id: "ValidationCode.errorWarningExpiredCodeTitle" });
    const errorWarningExpiredCodeDescription = intl.formatMessage({ id: "ValidationCode.errorWarningExpiredCodeDescription" });

    const errorWarningNetworkErrorTitle = intl.formatMessage({ id: "ValidationCode.errorWarningNetworkErrorTitle" });
    const errorWarningNetworkErrorDescription = intl.formatMessage({ id: "ValidationCode.errorWarningNetworkErrorDescription" });

    const toastDefaultErrorMessageTitle = intl.formatMessage({ id: "ValidationCode.toastDefaultErrorMessageTitle" });
    const toastDefaultErrorMessageDescription = intl.formatMessage({ id: "ValidationCode.toastDefaultErrorMessageDescription" });
    const { saveOnGa } = useGa();

    const { name, birthdate, contact, cpf } = profileData

    const requiredProfileInformations = useMemo(() => {
        return !!name && !!birthdate && !!contact && !!cpf
    }, [birthdate, contact, cpf, name])

    useEffect(() => {
        if(hasDoneProfileReq){
            setHasDoneProfileReq(false)
            getProfileInformations()

            if(!requiredProfileInformations) {
                renderNewStep("AlmostThere");
            }else if (isModal) {
                if (onCloseClick) {
                    onCloseClick();
                }
            } else {
                if (getParam("path")) {
                    window.location.href = `${Redirect(ctx.hosts)}${getParam("path")}`
                    return
                }
                window.location.href = `${Redirect(ctx.hosts)}/mytravels/reservations`;
            }

            setLoading(false);
        }
    }, [hasDoneProfileReq])

    const handleSubmit = async (data: ValidationCodeProps): Promise<void> => {
        setPreviousStep("ValidationCode");
        try {
            saveOnGa({
                event: "login_event",
                category: "Account",
                action: "Validation code",
                label: "validade code"
            });
            formRef.current?.setErrors({});
            const schema = Yup.object().shape({
                code: Yup.string().required(intl.formatMessage({ id: "ValidationCode.errorMessage" }))
            });

            await schema.validate(data, {
                abortEarly: false
            });
            const { code } = data;
            setLoading(true);
            try {
                if (authAmplify === "confirmSignUp") {
                    await Auth.confirmSignUp(user.email, code);
                    saveOnGa({
                        event: "login_event",
                        category: "Account",
                        action: "Registration",
                        label: "registeredUser ok"
                    });
                    if (!amplifyPwd) {
                        renderNewStep("Password");
                    } else {
                        const siginRes =  await Auth.signIn(user.email, amplifyPwd);
                        const storagedData = siginRes?.pool?.storage

                        for (let i = 0; i < storagedData.length; i += 1) {
                            const key = storagedData.key(i)
                            if (key.startsWith("CognitoIdentityServiceProvider") && key.endsWith("idToken")) {
                                setUserIdToken(storagedData.getItem(key));
                            }
                        }
                        if(featureToggles(ctx, "PROFILE")){
                            setHasDoneProfileReq(true)

                        } else if (isModal) {
                            if (onCloseClick) {
                                onCloseClick();
                            }
                        } else {
                            if (getParam("path")) {
                                window.location.href = `${Redirect(ctx.hosts)}${getParam("path")}`
                                return
                            }
                            window.location.href = `${Redirect(ctx.hosts)}/mytravels/reservations`;
                        }
                    }
                } else {
                    await Auth.forgotPasswordSubmit(user.email, code, amplifyTempPwd);
                    renderNewStep("ChangePassword");
                }
                saveOnGa({
                    event: "login_event",
                    category: "Account",
                    action: "Validation code",
                    label: "validateCode ok"
                });
            } catch (err) {
                //    para tratar
                // CodeMismatchException
                // @ts-ignore
                switch (err.code) {
                case "InvalidParameterException":
                    formRef.current?.setErrors({ code: errorWarningExpiredCodeTitle });
                    addToast({
                        type: "error",
                        title: errorWarningExpiredCodeTitle,
                        description: errorWarningExpiredCodeDescription,
                    });
                    break;
                case "NetworkError":
                    addToast({
                        type: "error",
                        title: errorWarningNetworkErrorTitle,
                        description: errorWarningNetworkErrorDescription,
                    });
                    break;
                case "CodeMismatchException":
                    formRef.current?.setErrors({ code: "C칩digo inv치lido." });
                    break;

                default:
                    addToast({
                        type: "error",
                        title: toastDefaultErrorMessageTitle,
                        description: toastDefaultErrorMessageDescription,
                    });
                    break;
                }
            }

            setLoading(false);

        } catch (error) {
            if (error instanceof Yup.ValidationError) {
                formRef.current?.setErrors(getValidationErrors(error));
            }
        }
    };

    const handleCodeNonReceived = () => {
        saveOnGa({
            event: "login_event",
            category: "Account",
            action: "Validation code",
            label: "action :: non receipt recovery code"
        });

        renderNewStep("CodeNonReceived");
    };

    if (loading) {
        return <Loading />
    }
    return <>
        <HeaderLogin isModal={isModal} previousStep="MainScreen"
        //  onCloseClick={onCloseClick}
        />
        <div className={classes.root}>
            <Title className={classes.title}>
                {translate({ id: "ValidationCode.title" })}
            </Title>
            <SubTitle className={classes.subTitle}>
                {translate({
                    id: "ValidationCode.subTitle",
                    value: {
                        email: <strong>{user ? user.email : ""}</strong>

                    },
                })}
            </SubTitle>

            <Form ref={formRef} handleSubmit={handleSubmit} >
                <Input type="text" name="code" placeholderText={translate({ id: "ValidationCode.typeCodeText" })} />

                <InternalButton type="submit" text={intl.formatMessage({ id: "ValidationCode.actionButton" })} />
            </Form>
            <Link style={{ textAlign: "center" }} className={classes.link} onMouseDown={handleCodeNonReceived}>
                <SubTitle variant="caption">
                    <b>{translate({ id: "ValidationCode.linkNoCodeReceived" })}</b>
                </SubTitle>
            </Link>

        </div>
        <Terms />
    </>

};

export default injectIntl(ValidationCode);
