import React, { useCallback } from "react";
import { useRenderSteps } from "../../../contexts/renderSteps"
import BackButton from "../BackButton";
import CloseButton from "../CloseButton";
import useStyles from "./styles";

interface HeaderLoginProps {
    onCloseClick?(): void;
    previousStep: string;
    isModal?: boolean;
}

const HeaderLogin: React.FC<HeaderLoginProps> = ({
    previousStep,
    onCloseClick,
    isModal
}) => {
    const classes = useStyles({});
    const { currentStep, renderNewStep } = useRenderSteps();

    const closeModal = useCallback(() => {
        if (onCloseClick) {
            onCloseClick();
        }
    }, [onCloseClick]);

    if(currentStep !== "MainScreen") {
        return (
            <div className={classes.headerLoginWrapper}>
                <div className={classes.actionButtons}>
                    {!!previousStep && currentStep !== "AlmostThere" &&
                        <button onClick={() => renderNewStep(previousStep)}>
                            <BackButton />
                        </button>
                    }

                    <button style={{marginLeft: "auto"}} onClick={closeModal}>
                        <CloseButton />
                    </button>
                </div>
            </div>)
    }

    return isModal ? (
        <div className={classes.headerLoginWrapper}>
            <div className={classes.actionButtons}>
                <button style={{ marginLeft: "auto" }} onClick={closeModal}>
                    <CloseButton />
                </button>
            </div>
        </div>
    ) : null
}

export default HeaderLogin;
