export const styles = theme => ({
    root: {
        background: "none",
        border: "1px solid #CACACA",
        borderRadius: "8px",
        lineHeight: "1.33",
        minHeight: "64px",
        padding: theme.spacing(3),
        textTransform: "none",
        width: "100%",
        "&:hover": {
            background: "none",
            borderColor: theme.palette.primary.main,
            color: theme.palette.primary.main,
        },
    },
    rootLabel: { color: theme.palette.text.primary },
    disabled: {
        background: "none !important",
        borderColor: `${theme.palette.text.disabled} !important`,
    },
    luggageLabel: { color: theme.palette.primary.main },
    disabledLabel: { color: theme.palette.greyscale[3] },
    matrix: { height: "68px" },
    isAmenities: {
        padding: `${theme.spacing(2)}px ${theme.spacing(3)}px`,
    },
    isActive: {
        background: theme.palette.primary.main,
        borderColor: theme.palette.primary.main,
        "&:hover": {
            background: theme.palette.primary.light,
            color: theme.palette.white.main,
        },
    },
    isActiveLabel: {
        color: theme.palette.white.main,
        "&:hover": { color: theme.palette.white.main },
    },
});
