import React, { forwardRef } from "react";
import useStyles from "./styles";

interface MessageValidationProps {
    message: string;
    isError?: boolean;
    isInfo?: boolean;
}

const MessageValidation = forwardRef<HTMLDivElement, MessageValidationProps>(
    ({ message, isError, isInfo }: MessageValidationProps, ref) => {

        const classes = useStyles({});

        return (
            <div
                ref={ref}
                className={`${classes.errorWrapper} ${isError ? classes.isError : classes.isInfo}`}
            >
                {message}
            </div>
        );
    });

export default MessageValidation;
