    var __STORY__ = "import React from "react";
import { Paper, Divider, } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles , MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import { StyledPre } from "../ColorsGuide/ColorsGuide.stories.styles"
import themes from "../Theme/themes";
import { BrandType } from "../types/brand";

const useStyles = makeStyles((theme) => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

type StoriesStoryProps = {
    mode: string,
    brand: BrandType
}

const StoriesStory = ({
    mode, brand
}: StoriesStoryProps) => {
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="stories">
                <Typography variant="h3">Como usar o BrandContext?</Typography>
                <br />
                <Divider />
                <br />
                <Typography variant="subtitle1">Componentes que utilizam o BrandContext, necessitam do provider do mesmo.</Typography>
                <Typography variant="subtitle1">Exemplo de código:</Typography>
                <StyledPre>
                    {`
                        const Page = () => {  
                            return (   
                                <BrandContext.Provider value={mockBrand}>
                                    <ThemeProviderCustom>
                                        <UserDataProviderCustom>           
                                            componente/página.....                      
                                        </UserDataProviderCustom>
                                    </ThemeProviderCustom>
                                </BrandContext.Provider>
                            )
                        };
                    `}
                </StyledPre>
                <Typography variant="subtitle1">Mock do objeto do Tema:</Typography>
