export const styles = theme => ({
    root: {
        border: `1px solid ${theme.palette.greyscale[3]}`,
        borderRadius: "100px",
        color: theme.palette.text.primary,
        position: "relative",
        textAlign: "center",
        transition: "all ease .5s",
        whiteSpace: "nowrap",
        [theme.breakpoints.between("md", "xl")]: {
            alignItems: "center",
            cursor: "pointer",
            display: "flex",
            height: "32px",
            justifyContent: "space-between",
            padding: theme.spacing(0, 2)
        }
    },
    header: {
        background: theme.palette.primary.main,
        color: theme.palette.white.main,
        margin: `${theme.spacing(-2)}px ${theme.spacing(-2)}px 0`,
        padding: `${theme.spacing(2)}px ${theme.spacing(2)}px ${theme.spacing(3)}px`,
        position: "relative",
        "&:before": {
            background: theme.palette.white.main,
            borderRadius: `${theme.spacing(2)}px ${theme.spacing(2)}px 0 0`,
            bottom: 0,
            content: "\" \"",
            height: theme.spacing(2),
            left: 0,
            position: "absolute",
            right: 0
        }
    },
    noModal: {
        border: "none",
        cursor: "auto",
        height: "auto",
        flexDirection: "column",
        textAlign: "left"
        // [theme.breakpoints.between("md", "xl")]: {
        //   "& >span": {
        //     width: `calc(100% + ${theme.spacing(4)}px)`,
        //   },
        // },
    },
    noModalMore: {
        [theme.breakpoints.between("xs", "md")]: {
            background: theme.palette.white.main,
            borderRadius: 0,
            height: "100%",
            left: 0,
            margin: "0 !important",
            overflow: "scroll",
            padding: theme.spacing(2),
            position: "absolute",
            right: 0,
            transition: "all ease .5s",
            top: 0,
            zIndex: theme.level.fixed
        }
    },
    active: {
        background: theme.palette.primary.dark,
        color: theme.palette.white.main,
        paddingRight: theme.spacing(4),
        transition: "all ease .5s"
    },
    opened: {
        background: theme.palette.primary.dark,
        color: theme.palette.white.main,
        cursor: "auto",
        transition: "all ease .5s"
    },
    iconOpened: {
        color: theme.palette.white.main,
        transition: "all ease .3s",
        transform: "rotate(180deg)"
    },
    iconClosed: {
        transition: "all ease .3s"
    },
    buttonDelete: {
        background: "transparent",
        border: 0,
        color: theme.palette.white.main,
        cursor: "pointer",
        outline: "none",
        marginRight: "-15px"
    },
    title: {
        display: "block",
        cursor: "pointer",
        padding: `${theme.spacing(1)}px ${theme.spacing(2)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            marginRight: theme.spacing(0.5),
            padding: `${theme.spacing(1)}px 0`
        }
    },
    container: {
        color: theme.palette.text.primary,
        height: 0,
        overflow: "hidden",
        position: "absolute",
        minWidth: "260px"
    },
    noModalContainer: {
        height: "auto",
        // overflow: "initial",
        position: "relative",
        [theme.breakpoints.between("md", "xl")]: {
            margin: "0 auto",
            maxWidth: "250px",
            minWidth: "auto"
        },
        [theme.breakpoints.between("xs", "md")]: {
            "li div >&": {
                padding: `0 ${theme.spacing(2)}px`
            }
        }
    },
    noModalContainerMobile: {
        paddingBottom: "72px"
    },
    grid: {
        minWidth: "360px"
    },
    open: {
        background: theme.palette.white.main,
        borderRadius: theme.spacing(2),
        boxShadow: theme.shadows[16],
        height: "auto",
        left: "-1px",
        padding: `${theme.spacing(2)}px ${theme.spacing(2)}px 0`,
        right: "-1px",
        textAlign: "left",
        top: "50px",
        zIndex: theme.level.modal
    },
    content: {
        marginBottom: theme.spacing(3),
        padding: 0,
        overflow: "auto"
    },
    contentMore: {
        margin: `0 ${theme.spacing(-2)}px`,
        padding: 0,
        overflow: "auto"
    },
    contentItem: { listStyle: "none" },
    item: { marginLeft: theme.spacing(1) },
    footer: {
        display: "flex",
        justifyContent: "space-between",
        [theme.breakpoints.between("xs", "md")]: {
            background: theme.palette.white.main,
            bottom: 0,
            left: 0,
            padding: theme.spacing(2),
            right: 0
        }
    },
    footerMobile: {
        opacity: 0,
        position: "fixed"
    },
    showFooterMobile: {
        opacity: 1
    },
    btn: {
        minWidth: 0,
        padding: 0
    },
    btnReset: {
        color: theme.palette.primary.main,
        float: "right"
    },
    btnApply: {
        color: theme.palette.primary.main,
        minWidth: 0,
        padding: 0,
        [theme.breakpoints.between("xs", "md")]: {
            color: theme.palette.text.primary
        }
    }
});
