export const styles = theme => ({
    root: {
        animation: "Loading 1s cubic-bezier(0.4, 0, 1, 1) infinite reverse",
        background:
      "linear-gradient(135deg,#EFEFEF 30%,#DDDDDD 38%,#DDDDDD 41%,#DDDDDD 50%,#DDDDDD 0,#DDDDDD 51%,#EFEFEF 57%,#EFEFEF 80%,#EFEFEF 0)",
        backgroundSize: "1200% 1200%",
        borderRadius: "7.5px",
        display: "block",
        minHeight: "16px",
        position: "relative",
        "&:before": {
            bottom: 0,
            content: "\"\"",
            left: 0,
            position: "absolute",
            right: 0,
            top: 0,
        },
    },
    absolute: {
        borderRadius: 0,
        height: "100%",
        left: 0,
        position: "absolute",
        top: 0,
        width: "100%",
    },
});
