import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import { User } from "@cvccorp-components/icons";
import CustomTypography from "../Typography";
import CustomTextField from "../TextField";
import CustomButton from "../Button";
import BG from "../assets/img/bg-motor.svg";
import PesqincHotel from "../Pesqinc/Hotel";
import FullCalendar from "../Calendar/FullCalendar";
import TopResume from "../TopResume";
import Container from "../Container";
import PaxHotel from "../Pax/Hotel";

const styles = theme => ({
    search: {
        background: theme.palette.primary.main,
        padding: `${theme.spacing(3)}px ${theme.spacing(3)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            background: `url(${BG}) no-repeat ${theme.palette.primary.main} bottom`,
            padding: `${theme.spacing(5)}px ${theme.spacing(4)}px ${theme.spacing(
                15
            )}px`,
        },
    },
    research: {
        background: theme.palette.primary.main,
        color: theme.palette.white.main,
        padding: `${theme.spacing(3)}px ${theme.spacing(3)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            padding: `${theme.spacing(2)}px ${theme.spacing(3)}px`,
        },
    },
    title: { color: theme.palette.white.main },
    btn: {
        margin: `${theme.spacing(1)}px 0`,
        width: "100%",
    },
    container: {
        marginLeft: theme.spacing(-1),
        marginTop: theme.spacing(3),
        width: `calc(100% + ${theme.spacing(2)}px)`,
    },
    researchContainer: {
        marginLeft: theme.spacing(-1),
        width: `calc(100% + ${theme.spacing(2)}px)`,
    },
    grid: {
        [theme.breakpoints.between("md", "xl")]: {
            padding: `0 ${theme.spacing(1)}px`,
        },
    },
    researchCity: { color: theme.palette.white.main },
    researchAddress: { color: theme.palette.white.main },
    researchDate: {
        borderRight: `1px solid ${theme.palette.white.alpha}`,
        color: theme.palette.white.main,
        paddingRight: theme.spacing(1),
        [theme.breakpoints.between("md", "xl")]: {
            borderLeft: `1px solid ${theme.palette.white.alpha}`,
            margin: `0 ${theme.spacing(1)}px 0 ${theme.spacing(4)}px`,
            paddingLeft: theme.spacing(3),
        },
    },
    researchPax: {
        color: theme.palette.white.main,
        margin: `0 ${theme.spacing(1)}px 0 0`,
        [theme.breakpoints.between("md", "xl")]: {
            margin: `0 ${theme.spacing(5)}px 0 ${theme.spacing(1)}px`,
        },
    },
    researchContent: { display: "inline-block" },
    researchContentWrapper: {
        [theme.breakpoints.between("xs", "md")]: {},
        [theme.breakpoints.between("md", "xl")]: {
            alignItems: "center",
            display: "flex",
        },
    },
    researchContentAddress: {
        display: "flex",
        flexDirection: "column",
        [theme.breakpoints.between("md", "xl")]: {
            margin: `0 ${theme.spacing(1)}px`,
        },
    },
    researchContentAddressContainer: {
        [theme.breakpoints.between("md", "xl")]: { display: "inline-block" },
    },
    researchContentAddressFull: {
        display: "flex",
        [theme.breakpoints.between("xs", "md")]: { marginBottom: theme.spacing(3) },
    },
    researchIcon: { marginLeft: theme.spacing(-0.5) },
    researchBtn: {
        [theme.breakpoints.between("xs", "md")]: {
            border: "none",
            paddingRight: 0,
        },
    },
    customTextPax: {
        "& > label": { transform: "translate(58px, 20px) scale(1)!important" },
    },
});

const mockHistory = [
    {
        name: "Arraial do Cabo",
        arrival: "06 de jun",
        departure: "09 de jun",
    },
    {
        name: "FlorianÃ³polis",
        arrival: "26 de mar",
        departure: " 28 de mar",
    },
    {
        name: "Hotel Majorelle Hostel",
        arrival: "26 de mar",
        departure: "28 de mar",
    },
];
const mockMostSearched = [
    { name: "Lisboa", price: "178" },
    { name: "Arraial do Cabo", price: "93" },
    { name: "Buenos Aires", price: "109" },
    { name: "Santiago", price: "143" },
    { name: "Porto de Galinhas", price: "143" },
];

let timerPesqinc = null;

const sortFunc = (a, b) => {
    if (a.type !== b.type && a.type === "location") return -1;
    if (a.type !== b.type && b.type === "hotel") return 1;
    return 0;
};

const SearchHotel = props => {
    const { classes } = props;
    const [search, setSearch] = useState([]);
    const [historySearch, setHistorySearch] = useState([]);
    const [mostSearched, setMostSearched] = useState([]);
    const [value, setValue] = useState("");

    const initialState = [{ value: null, adults: 1, children: 0, ages: [] }];
    const [open, setOpen] = useState(false);
    const [fields, setFields] = useState(initialState);
    const [valueTextField, setTextField] = useState({ value: "" });

    const getMostSearched = () => mockMostSearched;

    const getHistory = () => mockHistory;
    const getLocations = async text => {
        if (!text) return [];
        const searchText = text.replace(/ /g, "+");
        const payload = await fetch(
            `https://euhghjmvi6.execute-api.us-east-1.amazonaws.com/b2c_ti/locations?productType=HOT&q=${searchText}`
        );
        const result = await payload.json();
        return result.locations.map(item => {
            const pos = item.description.indexOf(" -");
            const name =
        pos > -1 ? item.description.split(" -")[0] : item.description;
            return {
                ...item,
                name: item.type === "HOTEL" ? name : item.city,
                address: `${item.state}, ${item.country}`,
                type: item.type === "CITY" ? "location" : "hotel",
            };
        });
    };

    useEffect(() => {
        setHistorySearch(getHistory);
        setMostSearched(getMostSearched);
    }, []);

    const changeText = text => {
        if (timerPesqinc) clearTimeout(timerPesqinc);
        timerPesqinc = setTimeout(async () => {
            const result = await getLocations(text);
            setSearch(result.length > 0 ? result.sort(sortFunc) : []);
        }, 500);
    };

    const handlePesqinc = e => {
        setValue(e.target.value);
        changeText(e.target.value);
    };

    const handleSelect = (item, selectedIndex) => {
        setValue(item.name);
    };

    const handleOpen = () => {
        if (typeof window !== "undefined") {
            if (props.isMobile) {
                document.body.classList.add("stop-scroll");
            }
        }
    };

    const handleClose = () => {
        if (typeof window !== "undefined") {
            if (props.isMobile) {
                document.body.classList.remove("stop-scroll");
            }
        }
    };

    const handleOpenPax = () => {
    // console.log("handleOpenPax");
        setOpen(!open);
    };

    const handleClosePax = () => {
        setOpen(false);
    };

    const handleChange = value => {
        setTextField({ value });
        setOpen(false);
    };

    const handleAdd = () => {
        const values = [...fields];
        values.push(initialState[0]);
        setFields(values);
    };

    const handleRemove = i => {
        const values = [...fields];
        values.splice(i, 1);
        setFields(values);
    };

    const handleRemoveRoom = () => {
        const values = [...fields];
        values.pop();
        setFields(values);
    };

    const handleAdults = (idx, adult) => {
        const adults = [...fields];
        adults[idx].adults = adult;
        setFields(adults);
    };

    const handleAddChildren = (fieldIdx, child) => {
        const children = [...fields];
        children[fieldIdx].children = child;
        children[fieldIdx].ages.push({ value: 0 });
        setFields(children);
    };

    const handleRemoveChildren = (fieldIdx, child) => {
        const children = [...fields];
        children[fieldIdx].children = child;
        children[fieldIdx].ages.pop();
        setFields(children);
    };

    const handleChildAge = (fieldIdx, ageIdx, e) => {
        const childAge = [...fields];
        childAge[fieldIdx].ages[ageIdx].value = e.target.value;
        setFields(childAge);
    };

    const handleClear = () => {
        setFields(initialState);
        setTextField({ value: "" });
    };

    const handleReset = () => {
        setValue("");
        changeText("");
    };

    return (
        <div className={props.research ? classes.research : classes.search}>
            <Container>
                {!props.research && (
                    <Grid container>
                        <Grid item lg={5} sm={12}>
                            <CustomTypography
                                type={props.isMobile ? "headline2" : "headline5"}
                                className={classes.title}
                            >
                OlÃ¡, confira os melhores hotÃ©is para seu conforto
                            </CustomTypography>
                        </Grid>
                    </Grid>
                )}
                {(props.researchActive || !props.research) && (
                    <Grid
                        container
                        className={
                            props.research ? classes.researchContainer : classes.container
                        }
                    >
                        <Grid item lg={4} xs={12} className={classes.grid}>
                            <PesqincHotel
                                search={search}
                                onChange={handlePesqinc}
                                onSelect={handleSelect}
                                isMobile={props.isMobile}
                                history={historySearch}
                                mostSearched={mostSearched}
                                value={value}
                                transparent
                                resetValue={handleReset}
                            />
                        </Grid>
                        <Grid item lg={3} xs={12} className={classes.grid}>
                            <FullCalendar
                                onDatesChange={dates => {}}
                                isMobile={props.isMobile}
                                error={false}
                                errorMsg={null}
                                onOpen={handleOpen}
                                onClose={handleClose}
                            />
                        </Grid>
                        <Grid item lg={3} xs={12} className={classes.grid}>
                            <PaxHotel
                                className={classes}
                                isMobile={false}
                                initialState={initialState}
                                open={open}
                                fields={fields}
                                onClick={handleOpenPax}
                                handleClose={handleClosePax}
                                handleAdd={handleAdd}
                                handleRemove={handleRemove}
                                handleRemoveRoom={handleRemoveRoom}
                                handleAdults={handleAdults}
                                handleAddChildren={handleAddChildren}
                                handleRemoveChildren={handleRemoveChildren}
                                handleChildAge={handleChildAge}
                                handleChange={handleChange}
                                valueTextField={valueTextField.value}
                                handleClear={handleClear}
                                error={false}
                                errorMsg=""
                            />
                        </Grid>
                        <Grid item lg={2} xs={12} className={classes.grid}>
                            <CustomButton
                                color="secondary"
                                label="Buscar hotÃ©is"
                                variant="contained"
                                size="large"
                                className={classes.btn}
                                name="Nome"
                            />
                        </Grid>
                    </Grid>
                )}
                {!props.researchActive && props.research && (
                    <TopResume
                        destination={props.destination}
                        withModal={props.withModal}
                    />
                )}
            </Container>
        </div>
    );
};

SearchHotel.defaultProps = { isMobile: false };
SearchHotel.propTypes = {
    classes: PropTypes.object.isRequired,
    research: PropTypes.bool,
    researchActive: PropTypes.bool,
    withModal: PropTypes.bool,
    destination: PropTypes.array,
    isMobile: PropTypes.bool,
};

export default withStyles(styles, { withTheme: true })(SearchHotel);
