/* eslint-disable indent */
import React, { useState, useEffect } from "react";
import MaskedInput from "react-text-mask";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";

// STYLES
import { styles } from "./styles";

const TextField = ({
	classes,
	label,
	value,
	id,
	type,
	autoComplete,
	labelClassName,
	inputClassName,
	rootClassName,
	size,
	icon,
	disabled,
	transparent,
	noBorder,
	error,
	errorMsg,
	onClick,
	onChange,
	onFocus,
	onBlur,
	onKeyUp,
	mask,
	inputName,
	currencyMask,
	placeholder,
	isActive,
	placeholderBold,
	labelOnBottom,
	required,
	readonly
}) => {
	const [active, setActive] = useState(false);
	const [internalValue, setValue] = useState(value);
	const [inputClasses, setInputClasses] = useState(undefined);

	useEffect(() => {
		const _inputClasses = `
        ${inputClassName || ""}
        ${size === "large" ? classes.large : classes.small}
        ${(isActive && !disabled) || (value && !disabled) || (internalValue && !disabled) ? classes.inputActive : ""}
        ${disabled ? classes.disabled : ""}
        ${readonly ? classes.readonly : ""}
        ${noBorder && !error ? classes.noBorder : ""}
        ${transparent ? classes.transparent : ""}
        ${icon ? classes.inputIcon : ""}
        ${error ? classes.error : ""}
    `;

		setActive(isActive);
		setInputClasses(_inputClasses);
	}, [isActive]);

	const rootClasses = `
    ${classes.root}
    ${rootClassName || ""}
    ${errorMsg && error ? classes.hasError : ""}
  `;

	const lbClasses = `
        ${labelClassName || ""}
        ${size === "large" ? classes.labelLarge : classes.label}
        ${icon ? (size === "large" ? classes.labelLargeIcon : classes.labelIcon) : ""}
        ${(active && !disabled) || (value && !disabled) || (internalValue && !disabled)
			? !value && labelOnBottom
				? classes.lblSemiActive
				: classes.lblActive
			: ""
		}
        ${transparent ? classes.lbtransparent : ""}
        ${disabled ? classes.disabledLabel : ""}
        ${placeholderBold && classes.lbBold}
        `;

	const iconClasses = `
    ${classes.icon}
    ${transparent ? classes.lbtransparent : ""}
    ${disabled ? classes.disabledLabel : ""}
    ${placeholderBold && classes.lbBold}
  `;

	const handleChange = e => {
		if (onChange) onChange(e);
		setValue(e.target.value);
	};

	const handleClick = e => {
		if (onClick) onClick(e);
		if (!disabled) setActive(true);
	};

	const handleFocus = e => {
		if (onFocus) onFocus(e);
		if (!disabled) setActive(true);
	};

	const handleBlur = e => {
		if (onBlur) onBlur(e);
		setActive(false);
	};

	const handleKeyUp = e => {
		if (onKeyUp) onKeyUp(e);
	};

	const labelToShow = label && <label className={lbClasses}> {label}</label>;

	const checkAutoComplete = () => autoComplete ? ({ autoComplete }) : {};

	return (
		<div className={rootClasses} onClick={handleClick}>
			{icon && <span className={iconClasses}>{icon}</span>}
			{disabled ? (value ? "" : labelToShow) : labelToShow}
			{mask ? (
				<MaskedInput
					mask={currencyMask}
					placeholder={placeholder}
					className={inputClasses}
					onChange={handleChange}
					value={value}
					type={type}
					id={id}
					onClick={handleClick}
					onFocus={handleFocus}
					onBlur={handleBlur}
					onKeyUp={handleKeyUp}
					disabled={disabled && !error}
					{...checkAutoComplete()}
				/>
			) : (
				<input
					id={id}
					placeholder={placeholder}
					onClick={handleClick}
					onChange={handleChange}
					onFocus={handleFocus}
					onBlur={handleBlur}
					onKeyUp={handleKeyUp}
					disabled={disabled && !error}
					value={value}
					name={inputName}
					className={inputClasses}
					type={type}
					required={required}
					{...checkAutoComplete()}
				/>
			)}
			{error && errorMsg && <span className={classes.errorText}>{errorMsg}</span>}
		</div>
	);
};

TextField.propTypes = {
	classes: PropTypes.object.isRequired,
	label: PropTypes.string,
	value: PropTypes.string,
	id: PropTypes.string,
	type: PropTypes.string,
	autoComplete: PropTypes.string,
	labelClassName: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
	inputClassName: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
	rootClassName: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
	size: PropTypes.string,
	icon: PropTypes.object,
	disabled: PropTypes.bool,
	labelOnBottom: PropTypes.bool,
	transparent: PropTypes.bool,
	noBorder: PropTypes.bool,
	error: PropTypes.bool,
	errorMsg: PropTypes.string,
	onClick: PropTypes.func,
	onChange: PropTypes.func,
	onFocus: PropTypes.func,
	onBlur: PropTypes.func,
	placeholderBold: PropTypes.bool,
	inputName: PropTypes.string,
	required: PropTypes.bool,
	onKeyUp: PropTypes.func
};

TextField.defaultProps = { type: "text" };

export default withStyles(styles, { withTheme: true })(TextField);
