import React from "react";
import { Paper, Divider } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import { makeStyles, MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf } from "@storybook/react";
import themes from "../../Theme/themes";
import { BrandType } from "../../types/brand";
import { Offers } from "@cvccorp-components/icons";
import ContentCard from "../../ContentCard";

const useStyles = makeStyles(theme => ({
    root: {
        background: theme.palette.background.default,
        boxShadow: theme.shadows[8],
        padding: "30px"
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px"
    }
}));

type CardStoryProps = {
    brand: BrandType;
    icon: boolean;
    title: string;
    titleHeight: string;
    text: string;
    link: string;
    href: string;
    mode: string;
};

const CardStory = ({ brand, icon, title, titleHeight, text, link, href, mode }: CardStoryProps) => {
    const classes = useStyles(themes[brand]);

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="checkbox">
                <Typography variant="h3">Controles de seleção</Typography>
                <br />
                <Divider />
                <br />
                <Grid container spacing={5}>
                    <Grid item xs={3}>
                        <ContentCard icon={icon && <Offers size="large" />} title={title} titleHeight={titleHeight} text={text} link={link} href={href} />
                    </Grid>
                </Grid>
            </Paper>
        </MuiThemeProvider>
    );
};

storiesOf("Components/Cards/ContentCard", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } }).add("Controls", args => <CardStory {...args} />, {
    args: {
        brand: "CVC",
        icon: true,
        title: "Title",
        titleHeight: "32px",
        text: "Body 1 — The same illusion which lifts the horizon of the sea to the level of the spectator on a hillside, the sable cloud beneath was dished out, and the car seemed to float in the middle of an immense dark sphere, whose",
        link: "Link",
        href: "#",
        mode: "root"
    },
    argTypes: {
        brand: {
            type: "select",
            options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
        },
        icon: { type: "boolean" },
        title: { type: "string" },
        titleHeight: { type: "string" },
        text: { type: "string" },
        link: { type: "string" },
        href: { type: "string" },
        mode: {
            type: "select",
            options: ["root", "darkBg"]
        }
    }
});
