import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import { Calendar, User, Air } from "@cvccorp-components/icons";
import Typography from "../Typography";
import Button from "../Button";
import BG from "../assets/img/bg-motor.svg";
import FullCalendar from "../Calendar/FullCalendar";
import PesqincPackage from "../Pesqinc/Package";
import PaxPackage from "../Pax/PaxPackage";
import Container from "../Container";
import PesqincAir from "../Pesqinc/Air/PesqincAir";

const styles = theme => ({
    search: {
        background: theme.palette.primary.main,
        padding: `${theme.spacing(3)}px ${theme.spacing(3)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            background: `url(${BG}) no-repeat ${theme.palette.primary.main} bottom`,
            padding: `${theme.spacing(5)}px ${theme.spacing(4)}px ${theme.spacing(
                15
            )}px`,
        },
    },
    research: {
        background: theme.palette.primary.main,
        padding: `${theme.spacing(3)}px ${theme.spacing(3)}px`,
        [theme.breakpoints.between("md", "xl")]: {
            padding: `${theme.spacing(2)}px ${theme.spacing(3)}px`,
        },
    },
    title: { color: theme.palette.white.main },
    btn: {
        margin: `${theme.spacing(1)}px 0`,
        width: "100%",
    },
    container: {
        marginLeft: theme.spacing(-1),
        marginTop: theme.spacing(3),
        width: `calc(100% + ${theme.spacing(2)}px)`,
    },
    researchContainer: {
        marginLeft: theme.spacing(-1),
        width: `calc(100% + ${theme.spacing(2)}px)`,
    },
    grid: {
        [theme.breakpoints.between("md", "xl")]: {
            padding: `0 ${theme.spacing(1)}px`,
        },
    },
    radioGroup: {
        flexDirection: "row",
        padding: `0 ${theme.spacing(1)}px`,
    },
    formControl: { marginRight: theme.spacing(3) },
    radioLabel: { color: theme.palette.white.main },
    multidestinationsArea: { padding: theme.spacing(1) },
    leg: { "& + &": { marginLeft: theme.spacing(1) } },
    addLeg: { margin: "5px 0" },
    researchOriginContainer: { display: "inline-block" },
    researchOrigin: {
        display: "flex",
        flexDirection: "column",
    },
    researchDestinyContainer: { display: "inline-block" },
    researchDestiny: {
        display: "flex",
        flexDirection: "column",
    },
    researchOriginDestinyContainer: {
        boxSizing: "border-box",
        display: "inline-block",
        outline: "none",
        minWidth: "250px",
    },
    researchOriginDestinyContainerFirst: {
        paddingRight: theme.spacing(4),
        position: "relative",
    },
    researchOriginDestinyContainerSecond: {
        padding: `0 ${theme.spacing(4)}px`,
        position: "relative",
        [theme.breakpoints.between("md", "xl")]: {
            "&:before": {
                content: "\"...\"",
                color: theme.palette.white.main,
                fontSize: "50px",
                left: "-15px",
                lineHeight: "6px",
                position: "absolute",
                top: "5px",
            },
        },
    },
    researchIcon: {
        color: theme.palette.white.main,
        left: "-15px",
        position: "absolute",
        top: "5px",
        [theme.breakpoints.between("md", "xl")]: { display: "none" },
    },
    researchOriginDestiny: {
        display: "flex",
        justifyContent: "space-between",
        position: "relative",
        [theme.breakpoints.between("xs", "md")]: { marginBottom: theme.spacing(3) },
    },
    researchOriginDestinyIcon: {
        color: theme.palette.white.main,
        left: "50%",
        position: "absolute",
        transform: "translate(-50%,-50%)",
        top: "50%",
    },
    text: { color: theme.palette.white.main },
    researchInfo: {
        display: "inline-block",
        [theme.breakpoints.between("xs", "md")]: { width: "calc(100% - 121px)" },
        [theme.breakpoints.between("md", "xl")]: {
            borderLeft: `1px solid ${theme.palette.white.alpha}`,
            margin: `0 ${theme.spacing(2)}px`,
            paddingLeft: theme.spacing(2),
        },
    },
    researchPax: {
        [theme.breakpoints.between("xs", "md")]: {
            borderLeft: `1px solid ${theme.palette.white.alpha}`,
            marginLeft: theme.spacing(1),
        },
    },
    researchBtn: {
        [theme.breakpoints.between("xs", "md")]: {
            border: "none",
            paddingRight: 0,
        },
    },
    researchMultidestination: {
        [theme.breakpoints.between("md", "xl")]: {
            display: "inline-block",
            maxWidth: "50%",
            verticalAlign: "middle",
        },
    },
    pesqincContainer: { position: "relative" },
    iconAH: {
        background: theme.palette.primary.main,
        borderRadius: "100%",
        color: theme.palette.white.main,
        position: "absolute",
        left: "50%",
        top: "35px",
        transform: "translate(-50%, -50%)",
        zIndex: 2,
    },
    originField: {
        paddingRight: theme.spacing(0.5),
        position: "relative",
    },
    destinyField: {
        paddingLeft: theme.spacing(0.5),
        position: "relative",
    },
    labelOrigin: {
        left: "14px",
        position: "absolute",
        top: "13px",
    },
    labelDestiny: {
        right: "14px",
        position: "absolute",
        top: "13px",
    },
});

const mockHistory = [
    {
        name: "Arraial do Cabo",
        arrival: "06 de jun",
        departure: "09 de jun",
    },
    {
        name: "FlorianÃ³polis",
        arrival: "26 de mar",
        departure: " 28 de mar",
    },
    {
        name: "Hotel Majorelle Hostel",
        arrival: "26 de mar",
        departure: "28 de mar",
    },
];

const mockMostSearched = [
    { name: "Lisboa", price: "178" },
    { name: "Arraial do Cabo", price: "93" },
    { name: "Buenos Aires", price: "109" },
    { name: "Santiago", price: "143" },
    { name: "Porto de Galinhas", price: "143" },
];

let timerPesqinc = null;

const sortFunc = (a, b) => {
    if (a.type !== b.type && a.type === "location") return -1;
    if (a.type !== b.type && b.type === "hotel") return 1;
    return 0;
};

const SearchPackage = props => {
    const { classes } = props;
    const [destiny, setDestiny] = useState("");
    const [origin, setOrigin] = useState("");
    const [originItem, setOriginItem] = useState("");
    const [destinyItem, setDestinyItem] = useState("");
    const [search, setSearch] = useState([]);
    const [historySearch, setHistorySearch] = useState([]);
    const [mostSearched, setMostSearched] = useState([]);
    const initialState = {
        input: [{ value: null, adults: 1, children: 0, ages: [] }],
        classes: "eco",
        directFlight: false,
    };
    const [openPax, setOpenPax] = useState(false);
    const [fieldsPax, setFieldsPax] = useState(initialState);
    const [valueTextFieldPax, setTextFieldPax] = useState({ value: "" });

    const handleOpenPax = () => {
        setOpenPax(!openPax);
    };

    const handleClosePax = () => {
        setOpenPax(!openPax);
    };

    const isValid = () => {
        let boolValid = true;
        setFieldsPax({
            ...fieldsPax,
            input: fieldsPax.input.map(field => ({
                ...field,
                ages: field.ages.map(age => {
                    const validated = age.value === 0;
                    boolValid = boolValid && !validated;
                    return {
                        ...age,
                        isValid: validated,
                    };
                }),
            })),
        });
        return boolValid;
    };

    const handleChangePax = value => {
        if (isValid()) {
            setTextFieldPax({ value });
            handleOpenPax();
        }
    };

    const handleAddPax = () => {
        const values = { ...fieldsPax };
        values.input.push(initialState.input[0]);
        setFieldsPax(values);
    };

    const handleRemovePax = i => {
        const values = { ...fieldsPax };
        values.input.splice(i, 1);
        setFieldsPax(values);
    };

    const handleRemoveRoom = () => {
        const values = { ...fieldsPax };
        values.input.pop();
        setFieldsPax(values);
    };

    const handleAdults = (idx, adult) => {
        const adults = { ...fieldsPax };
        adults.input[idx].adults = adult;
        setFieldsPax(adults);
    };

    const handleAddChildren = (fieldIdx, child) => {
        const children = { ...fieldsPax };
        children.input[fieldIdx].children = child;
        children.input[fieldIdx].ages.push({ value: 0 });
        setFieldsPax(children);
    };
    const handleRemoveChildren = (fieldIdx, child) => {
        const children = { ...fieldsPax };
        children.input[fieldIdx].children = child;
        children.input[fieldIdx].ages.pop();
        setFieldsPax(children);
    };

    const handleChildAge = (fieldIdx, ageIdx, e) => {
        const childAge = { ...fieldsPax };
        childAge.input[fieldIdx].ages[ageIdx].value = e.target.value;
        setFieldsPax(childAge);
        isValid();
    };
    const handleClear = () => {
        setFieldsPax(initialState);
        setTextField({ value: "" });
    };

    const handleClasses = field => {
        const values = { ...fieldsPax };
        values.classes = field;
        setFieldsPax(values);
    };
    const handleDirectFlight = () => {
        const values = { ...fields };
        values.directFlight = !values.directFlight;
        setFieldsPax(values);
    };

    const leg = (item, i) => (
        <span
            key={i}
            className={`
        ${classes.researchOriginDestinyContainer}
        ${
        i === 0
            ? classes.researchOriginDestinyContainerFirst
            : i
                ? classes.researchOriginDestinyContainerSecond
                : ""
        }
        `}
        >
            {i > 0 && (
                <span className={classes.researchIcon}>
                    <Air size="large" />
                </span>
            )}
            <span className={classes.researchOriginDestiny}>
                <span className={classes.researchOriginDestinyIcon}>
                    <Air size="large" />
                </span>
                <span className={classes.researchOriginContainer}>
                    <span className={classes.researchOrigin}>
                        <Typography
                            type={props.isMobile ? "headline5" : "headline2"}
                            className={classes.text}
                        >
                            {item.originIata}
                        </Typography>
                        <Typography type="body2regular" className={classes.text}>
                            {item.origin}
                        </Typography>
                    </span>
                </span>
                <span className={classes.researchDestinyContainer}>
                    <span className={classes.researchDestiny}>
                        <Typography
                            type={props.isMobile ? "headline5" : "headline2"}
                            className={classes.text}
                        >
                            {item.destinyIata}
                        </Typography>
                        <Typography type="body2regular" className={classes.text}>
                            {item.destiny}
                        </Typography>
                    </span>
                </span>
            </span>
        </span>
    );

    const getMostSearched = () => mockMostSearched;

    const getHistory = () => mockHistory;

    const getLocations = async text => {
        if (!text) return [];
        const searchText = text.replace(/ /g, "+");
        const payload = await fetch(
            `https://euhghjmvi6.execute-api.us-east-1.amazonaws.com/b2c_ti/locations?productType=AIR&q=${searchText}`
        );
        const result = await payload.json();
        return result.locations
            .filter(item => !!item.IATA)
            .map((item, i) => ({
                ...item,
                key: { i },
                name: `${item.city}, ${item.state}`,
                address: item.description,
                type: item.type === "CITY" ? "location" : item.type.toLowerCase(),
            }));
    };

    useEffect(() => {
        setHistorySearch(getHistory);
        setMostSearched(getMostSearched);
    }, []);

    const changeText = text => {
        if (text && text.length < 3) return;
        if (timerPesqinc) clearTimeout(timerPesqinc);
        timerPesqinc = setTimeout(async () => {
            const result = await getLocations(text);
            setSearch(result.length > 0 ? result.sort(sortFunc) : []);
        }, 500);
    };

    const handlePesqinc = (e, tag) => {
        changeText(e.target.value);
        if (tag === "origin") setOrigin(e.target.value);
        else if (tag === "destiny") setDestiny(e.target.value);
    };

    const handleSelect = (item, tag) => {
        if (tag === "origin") {
            setOrigin(item.name);
            setOriginItem(item);
        } else if (tag === "destiny") {
            setDestiny(item.name);
            setDestinyItem(item);
        }
    };

    const handleOpen = () => {
        if (typeof window !== "undefined") {
            if (props.isMobile) {
                document.body.classList.add("stop-scroll");
            }
        }
    };

    const handleClose = () => {
        if (typeof window !== "undefined") {
            if (props.isMobile) {
                document.body.classList.remove("stop-scroll");
            }
        }
    };
    // const handleClose = tag => {
    //   if (tag === "origin" && !originItem) setOrigin("");
    //   else if (tag === "destiny" && !destinyItem) setDestiny("");
    // };

    const handleReset = (item, tag) => {
        if (tag === "origin") {
            setOrigin("");
            setOriginItem(item);
        } else if (tag === "destiny") {
            setDestiny("");
            setDestinyItem(item);
        }
        changeText("");
    };

    return (
        <div className={props.research ? classes.research : classes.search}>
            <Container>
                {!props.research && (
                    <Grid container>
                        <Grid item lg={5}>
                            <Typography
                                type={props.isMobile ? "headline5" : "headline2"}
                                className={classes.title}
                            >
                OlÃ¡, confira os melhores pacotes para o seu conforto
                            </Typography>
                        </Grid>
                    </Grid>
                )}
                {(props.researchActive || !props.research) && (
                    <Grid
                        container
                        className={
                            props.research ? classes.researchContainer : classes.container
                        }
                    >
                        <Grid item lg={4} xs={12} className={classes.grid}>
                            <Grid container className={classes.pesqincContainer}>
                                <Grid item xs={6} className={classes.originField}>
                                    <PesqincPackage
                                        value={origin}
                                        iata={(originItem && originItem.IATA) || "Origem"}
                                        label="De onde vai sair?"
                                        search={search}
                                        onChange={item => handlePesqinc(item, "origin")}
                                        onSelect={item => handleSelect(item, "origin")}
                                        onClose={() => handleClose("origin")}
                                        isMobile={props.isMobile}
                                        history={historySearch}
                                        mostSearched={mostSearched}
                                        resetValue={e => handleReset(e, "origin")}
                                    />
                                </Grid>
                                <span className={classes.iconAH}>
                                    <Air size="medium" />
                                </span>
                                <Grid item xs={6} className={classes.destinyField}>
                                    <PesqincPackage
                                        value={destiny}
                                        iata={(destinyItem && destinyItem.IATA) || "Destino"}
                                        label="Pra onde vai?"
                                        search={search}
                                        onChange={item => handlePesqinc(item, "destiny")}
                                        onSelect={item => handleSelect(item, "destiny")}
                                        isMobile={props.isMobile}
                                        history={historySearch}
                                        mostSearched={mostSearched}
                                        alignRight
                                        resetValue={e => handleReset(e, "destiny")}
                                    />
                                </Grid>
                            </Grid>
                        </Grid>
                        <Grid item lg={3} xs={12} className={classes.grid}>
                            <FullCalendar
                                onDatesChange={dates => {}}
                                isMobile={props.isMobile}
                                error={false}
                                errorMsg={null}
                                onOpen={handleOpen}
                                onClose={handleClose}
                            />
                        </Grid>
                        <Grid item lg={3} xs={12} className={classes.grid}>
                            <PaxPackage
                                initialState={initialState}
                                open={openPax}
                                fields={fieldsPax}
                                onClick={handleOpenPax}
                                handleClose={handleClosePax}
                                handleAdd={handleAddPax}
                                handleRemove={handleRemovePax}
                                handleRemoveRoom={handleRemoveRoom}
                                handleAdults={handleAdults}
                                handleAddChildren={handleAddChildren}
                                handleRemoveChildren={handleRemoveChildren}
                                handleChildAge={handleChildAge}
                                handleChange={handleChangePax}
                                valueTextField={valueTextFieldPax.value}
                                handleClear={handleClear}
                                handleClasses={handleClasses}
                                handleDirectFlight={handleDirectFlight}
                                isMobile={false}
                                disabledExe
                                disabledEco
                            />
                        </Grid>
                        <Grid item lg={2} xs={12} className={classes.grid}>
                            <Button
                                color="secondary"
                                label="Buscar voos"
                                variant="contained"
                                size="large"
                                className={classes.btn}
                            />
                        </Grid>
                    </Grid>
                )}
                {!props.researchActive && props.research && (
                    <Grid container>
                        <Grid item className={classes.researchContentWrapper} xs={12}>
                            {props.destination.length === 1 &&
                props.destination.map((item, i) => (
                    <span key={i} className={classes.researchContent}>
                        {leg(item)}
                        <Typography
                            type="body2regular"
                            className={`${classes.text} ${classes.researchInfo}`}
                        >
                            <span className={classes.researchDate}>
                                <Calendar /> {item.departure} - {item.arrival}
                            </span>
                            <span className={classes.researchPax}>
                                <User /> {item.pax}
                            </span>
                        </Typography>
                    </span>
                ))}
                            {props.destination.length > 1 && (
                                <span className={classes.researchContent}>
                                    <span className={classes.researchMultidestination}>
                                        <Slider
                                            infinite={false}
                                            speed={500}
                                            slidesToShow={props.isMobile ? 1.4 : 2}
                                            touchThreshold={100}
                                            arrows={false}
                                            dots={false}
                                        >
                                            {props.destination.map((item, i) => leg(item, i))}
                                        </Slider>
                                    </span>
                                    <Typography
                                        type="body2regular"
                                        className={`${classes.text} ${classes.researchInfo}`}
                                    >
                                        <span className={classes.researchDate}>
                                            <Calendar /> {props.destination[0].departure} -{" "}
                                            {props.destination[props.destination.length - 1].arrival}
                                        </span>
                                        <span className={classes.researchPax}>
                                            <User /> {props.destination[0].pax}
                                        </span>
                                    </Typography>
                                </span>
                            )}
                            <Button
                                className={classes.researchBtn}
                                color="secondary"
                                label="Alterar busca"
                                variant="outlined"
                                size="small"
                            />
                        </Grid>
                    </Grid>
                )}
            </Container>
        </div>
    );
};

SearchPackage.propTypes = {
    classes: PropTypes.object.isRequired,
    research: PropTypes.bool,
    destination: PropTypes.array,
    isMobile: PropTypes.bool,
    researchActive: PropTypes.bool,
    type: PropTypes.string,
};

export default withStyles(styles, { withTheme: true })(SearchPackage);
