export const styles = theme => ({
    root: {
        borderRadius: "100px",
        fontFamily: theme.font.semibold,
        fontSize: "14px",
        height: "24px",
        overflow: "hidden",
    },
    icon: {
        background: `${theme.palette.spot.main}`,
        color: `${theme.palette.white.main}`,
        height: `${24 - theme.spacing(1)}px`,
        margin: `0 -${theme.spacing(1)}px 0 0`,
        // padding: `${theme.spacing(1) / 2}px `,
        width: `${24 - theme.spacing(1)}px`,
    },
    label: {
        padding: `0 ${theme.spacing(1)}px`,
        "&:empty": { display: "none" },
    },
    spot: {
        background: `${theme.palette.spot.main}`,
        color: `${theme.palette.white.main}`,
    },
    white: {
        background: `${theme.palette.white.main}`,
        boxShadow: `${theme.shadows[0]}`,
        color: `${theme.palette.text.secondary}`,
    },
    hotel: {
        background: `${theme.palette.hotel.main}`,
        color: `${theme.palette.white.main}`,
    },
    air: {
        background: `${theme.palette.primary.main}`,
        color: `${theme.palette.white.main}`,
    },
    package: {
        background: `${theme.palette.package.main}`,
        color: `${theme.palette.white.main}`,
    },
    hasIcon: {
        paddingLeft: "0",
        "&:empty": { display: "none" },
    },
});
