import React, { useState, useEffect } from "react";
import _ from "lodash";
import PropTypes from "prop-types";
import DropDown from "../DropDown";

const OrderEngine = ({ orders, dataSource, onOrdered, className }) => {
    const [itemSelected, setItemSelected] = useState();
    const [menu, setMenu] = useState([]);

    useEffect(() => {
        const menuItems = [];
        orders.forEach(order => {
            if (order.selected) setItemSelected(order);
            menuItems.push(order.value);
        });
        setMenu(menuItems);
    }, []);

    const filtred = sel => {
        const inOrder = _.orderBy(dataSource, sel.field, sel.operation);
        onOrdered(inOrder, sel);
    };

    const handleSelect = v => {
        const sel = orders.find(el => el.value === v);
        if (dataSource) filtred(sel);
        else onOrdered(sel);
    };

    return (
        <DropDown
            onClick={sel => handleSelect(sel)}
            selected={itemSelected && itemSelected.selected ? itemSelected.value : ""}
            menu={menu}
            className={className}
        />
    );
};

OrderEngine.defaultProps = {};
OrderEngine.propTypes = {
    orders: PropTypes.array,
    onOrdered: PropTypes.func,
    className: PropTypes.string,
};

export default OrderEngine;
