import React from "react";
import { User } from "@cvccorp-components/icons";
import classNames from "classnames";
import useStyles from "./styles";

interface IUserAreaInfo {
    text?: string | React.ReactNode
    withIcon?: boolean
    rtl?: boolean
    isMobile?: boolean
}

const UserAreaInfo = ({
    text,
    withIcon = true,
    rtl,
    isMobile
}: IUserAreaInfo) => {
    const classes = useStyles({});

    return (
        <div className={classNames(classes.containerIcon, {
            [classes.mobile]: isMobile
        })}>
            {withIcon && <div className={classNames(classes.icon, {
                [classes.iconRight]: rtl
            })}>
                <User size="medium" />
            </div>}
            {text}
        </div>
    )
}

export default UserAreaInfo
