import React from "react";
import PropTypes from "prop-types";
import Grid from "@material-ui/core/Grid";
import "slick-carousel/slick/slick.css";
import Air from "@cvccorp-components/icons/dist/Air";
import AirBack from "@cvccorp-components/icons/dist/AirBack";
import SingleBaggage from "@cvccorp-components/icons/dist/SingleBaggage"
import SingleLuggage from "@cvccorp-components/icons/dist/SingleLuggage"
import User from "@cvccorp-components/icons/dist/User"
import { withStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import Slider from "react-slick";
import Box from "../Box";
import LoadingContent from "../LoadingContent";
import Stars from "../Stars";
import Tag from "../Tag";
import Typography from "../Typography";
import "../assets/css/slick-theme.css";

import { styles } from "./styles";

const Card = ({ classes, product, imgs, notAvaliable, isLoading, onClick, className, small, subtitle, title, arrival, departure, totalDays, stars, price, taxesIncluded, parcel, altImg, onKeyPress, shipmentTaxes, items,  }) => {
    const getProduct = () => {
        switch (product) {
        case "air":
            return "Aéreo";
        case "hotel":
            return "Hotel";
        case "package":
            return "Pacote";
        case "resort":
            return "Resorts";
        case "car":
            return "Carros";
        default:
        }
    };

    const getPriceLabel = () => {
        switch (product) {
        case "air":
            return "Passagens";
        case "hotel":
            return "Diárias";
        case "package":
            return "Pacotes";
        case "resort":
            return "Diárias";
        case "car":
            return "Diárias";
        default:
        }
    };

    const ImgWpr = props => {
        if (!props.isSlider) return props.children;
        return (
            <Slider infinite={false} speed={500} slidesToShow={1} touchThreshold={100} arrows accessibility dots>
                {props.children}
            </Slider>
        );
    };

    const getImgs = container => (
        <div className={classes.imgContainer}>
            <ImgWpr isSlider={imgs && imgs.length > 1}>
                {imgs.map((item, i) => (
                    <span className={classes[container]} key={i}>
                        <img className={notAvaliable ? clsx(classes.notAvaliableImg, classes.img) : classes.img} src={item} alt={altImg || title} />
                    </span>
                ))}
            </ImgWpr>
        </div>
    );

    const getIcon = (icon) => {
        const icons = {
            person: <User />,
            bag: <SingleBaggage />,
            weight: <SingleLuggage />
        }

        return icons[icon];
    }
    return (
        <Box onKeyPress={onKeyPress} className={clsx(classes.root, classes[product], className)} onClick={onClick}>
            {!isLoading && (
                <div>
                    <div className={imgs ? (small ? classes.headerSmall : classes.header) : classes.headerNoImg}>
                        <div className={classes.tagContainer}>
                            {imgs && getImgs(small ? "imgContainerPromoSmall" : "imgContainerPromo")}
                            {product && <Tag className={imgs ? classes.tag : ""} label={getProduct()} color={product} />}
                        </div>
                        <Typography className={classes.title} type="headline6">
                            {title}
                        </Typography>
                        {subtitle && (
                            <Typography className={classes.subtitle} type="body2regular">
                                {subtitle}
                            </Typography>
                        )}
                        {arrival && departure && product === "package" && (
                            <Typography className={classes.date} type="body2regular">
                                <Air className={classes.icon} size="medium" />
                                {departure}
                                {!small && " - "}
                                <span className={small && classes.break}>
                                    {small && <AirBack className={classes.icon} size="medium" />}
                                    {arrival}
                                </span>
                            </Typography>
                        )}
                        {departure && product === "air" && (
                            <Typography className={classes.departure} type="body2regular">
                                <Air className={classes.icon} size="medium" />
                                {departure}
                            </Typography>
                        )}
                        {arrival && product === "air" && (
                            <Typography className={classes.arrival} type="body2regular">
                                <AirBack className={classes.icon} size="medium" />
                                {arrival}
                            </Typography>
                        )}
                        {totalDays && (
                            <Typography className={classes.totalDays} type="body2regular">
                                {totalDays} diárias
                            </Typography>
                        )}
                        {items && items.length > 0 && product ==="car" && (
                            <div className={classes.items}>
                                {items.map((item, i) => (
                                    <div className={classes.item} key={i}>
                                        <i className={small ? classes.smallItemIcon : classes.itemIcon}>{getIcon(item.icon)}</i>
                                        <Typography className={classes.item}  type="body2regular">
                                            {item.info}
                                        </Typography>
                                    </div>
                                ))}
                            </div>
                        )}
                        {stars && <Stars className={classes.stars} stars={stars} size="smaller" />}
                    </div>
                    <Grid container>
                        {price && (
                            <Grid item xs={12}>
                                <div className={classes.priceContainer}>
                                    <Typography type="captionregular">
                                        {getPriceLabel()}
                                        <span className={classes.priceLabel}>a partir de</span>
                                    </Typography>
                                    <Typography type="body2regularsemibold">R$</Typography> <Typography type="headline3">{price}</Typography>
                                </div>
                            </Grid>
                        )}
                    </Grid>
                    {(taxesIncluded || parcel || product === "car") && !small ? (
                        <div className={classes.footer}>
                            {taxesIncluded && (
                                <Typography className={classes.footerItem} type="captionsemibold">
                                    Taxas inclusas
                                </Typography>
                            )}
                            {parcel && (
                                <Typography className={classes.footerItem} type="captionregular">
                                    Em até {parcel}x
                                </Typography>
                            )}
                            {product ==="car" && (
                                <Typography className={classes.footerItem} type="captionregular">
                                    Taxa de remessa: <strong>{shipmentTaxes === 0 ? "Isento" : shipmentTaxes}</strong>
                                </Typography>
                            )}
                        </div>
                    ) : (
                        <></>
                    )}
                </div>
            )}
            {isLoading ? (
                <div>
                    <Grid container>
                        <Grid item xs={12}>
                            <div className={classes.imgContainer}>
                                <div className={classes.imgContainerPromoSmall}>
                                    <LoadingContent absolute />
                                </div>
                            </div>
                        </Grid>
                    </Grid>
                    <Grid container className={classes.loadingContent}>
                        <Grid item xs={11}>
                            <LoadingContent />
                        </Grid>
                    </Grid>
                    <Grid container className={classes.loadingContent}>
                        <Grid item xs={9}>
                            <LoadingContent />
                        </Grid>
                    </Grid>
                    <Grid container className={classes.loadingContent}>
                        <Grid item xs={3}>
                            <LoadingContent />
                        </Grid>
                    </Grid>
                    <Grid container className={classes.loadingPrice}>
                        <Grid item xs={1}>
                            <LoadingContent />
                        </Grid>
                    </Grid>
                </div>
            ) : (
                <></>
            )}
        </Box>
    );
};

Card.propTypes = {
    arrival: PropTypes.string,
    classes: PropTypes.object.isRequired,
    className: PropTypes.object,
    departure: PropTypes.string,
    imgs: PropTypes.array,
    altImg: PropTypes.string,
    isLoading: PropTypes.bool,
    notAvaliable: PropTypes.bool,
    onClick: PropTypes.func,
    onKeyPress: PropTypes.func,
    parcel: PropTypes.string,
    price: PropTypes.string,
    product: PropTypes.string,
    small: PropTypes.bool,
    stars: PropTypes.number,
    subtitle: PropTypes.string,
    taxesIncluded: PropTypes.bool,
    title: PropTypes.string,
    totalDays: PropTypes.oneOfType([PropTypes.number, PropTypes.string])
};

export default withStyles(styles, { withTheme: true })(Card);
