export const styles = theme => ({
    item: {
        marginTop: theme.spacing(2),
        "& + &": {
            borderTop: `1px solid ${theme.palette.grey[50]}`,
            marginTop: theme.spacing(4),
            paddingTop: theme.spacing(2),
        },
    },
    matrixBestPrice: { color: theme.palette.primary.main },
    matrixPrice: {
        display: "block",
        marginTop: theme.spacing(0.5),
    },
    matrixBestPriceIcon: {
        background: theme.palette.spot.main,
        color: theme.palette.white.main,
        borderRadius: "100%",
        fill: "none",
        marginRight: theme.spacing(0.5),
    },
    matrixBestPriceTag: {
        bottom: "-12px",
        left: "50%",
        position: "absolute",
        transform: "translateX(-50%)",
    },
    img: {
        marginBottom: theme.spacing(1.3),
        marginLeft: theme.spacing(-1.5),
        verticalAlign: "middle",
        maxHeight: 28,
        maxWidth: 28,
    },
    moreBtn: { marginTop: theme.spacing(1) },
    imgLoading: {
        display: "inline-block",
        height: theme.spacing(4),
        marginRight: theme.spacing(1),
        verticalAlign: "middle",
        width: theme.spacing(4),
    },
    nameLoading: {
        display: "inline-block",
        verticalAlign: "middle",
        width: `calc(100% - ${theme.spacing(5)}px)`,
    },
    labelLoading: {
        margin: `0 auto ${theme.spacing(1)}px`,
        width: "48px",
    },
    priceLoading: { width: "64px" },

    flightHeader: {
        alignItems: "center",
        display: "flex",
        height: 50,
        justifyContent: "center",
    },
    flightHighlight: {
        "&:hover": { background: "transparent" },
        height: 50,
        display: "flex",
        padding: 0,
        border: "none",
        borderRadius: 0,
        width: "100%",
        background: "none",
    },
    columnLine: {
        borderLeft: "1px solid #DDD",
        maxWidth: "99.666667% !important",
    },
    highlightCell: {
        background: "hsla(0,0%,86.7%,.24)",
    },
    highlightTitleStops: {
        background: "hsla(0,0%,86.7%,.24)",
        borderRadius: "16px 0 0 16px",
    },
    slider: {
        width: "80px",
        flexGrow: 1,
        "& .slick-slider": {
            position: "relative",
        },
        "& .slick-prev": {
            background:
        "url(https://static.submarinoviagens.com.br/passagens/_next/static/images/arrowLeftLightRegular-cdef2f5aed1c214a4ad5d35d8ebe6589.svg) no-repeat 50% #fff",
            borderRadius: "16px",
            boxShadow: "0 0 16px 0 rgba(0, 0, 0, .1)",
            height: 40,
            width: 40,
            left: "-22px",
        },
        "& .slick-next": {
            background:
        "url(https://static.submarinoviagens.com.br/passagens/_next/static/images/arrowRightLightRegular-7ecf75038edcbb182199ed9df408b35f.svg) no-repeat 50% #fff",
            borderRadius: "16px",
            boxShadow: "0 0 16px 0 rgba(0, 0, 0, .1)",
            height: 40,
            width: 40,
            right: 0,
        },
        "& .slick-track": {
            marginLeft: 0,
            display: "flex",
        },
        "& .slick-list": {
            padding: "0 0 12px",
            overflow: "hidden",
        },
        "& .slick-disabled": {
            display: "none !important",
        },
    },
});
