import React, { createContext, useCallback, useContext, useState } from "react";
import { UserDataProvider } from "../shared/hooks/UserContext";
import { AuthAmplifyProvider } from "./AmplifyContext";
import { LoadingProvider } from "./LoadingContext";
import { RenderStepsProvider, useRenderSteps } from "./renderSteps";
import { ToastProvider } from "./Toast";

interface iLoginContext{
    isModal:boolean;
    onCloseClick():void
}
const LoginContext = createContext<iLoginContext>({} as iLoginContext);

const LoginProvider: React.FC<any> = ({ modal,closeClick,children }) => {
    const {currentStep} = useRenderSteps()
    const [isModal, setIsModal] = useState<boolean>(modal);
    const onCloseClick = useCallback(()=>{
        if(currentStep !== "AlmostThere"){
            closeClick();
        }
    },[closeClick, currentStep]);
    return (
        <LoginContext.Provider value={{ isModal, onCloseClick }}>
            {children}
        </LoginContext.Provider>
    );
};

export function useLoginContext():iLoginContext {
    const context = useContext(LoginContext);
    return context;
}

const AppProvider: React.FC<any> = ({ isModal,onCloseClick,children }) => (
    <UserDataProvider>
        <LoadingProvider>
            <AuthAmplifyProvider>
                <RenderStepsProvider>
                    <LoginProvider modal={isModal} closeClick={onCloseClick}>
                        <ToastProvider>
                            {children}
                        </ToastProvider>
                    </LoginProvider>
                </RenderStepsProvider>
            </AuthAmplifyProvider>
        </LoadingProvider>
    </UserDataProvider>

);

export default AppProvider;
