import React, { useContext } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import { Progress } from "react-sweet-progress";
import Typography from "../Typography";
import Box from "../Box";
import { styles } from "./styles";
import { BrandContext } from "../BrandContext/BrandContext";

// rate: número de 0 a 10

const Rate = ({ classes, rate }) => {
    // TODO rever implementação do contexto
    const ctx = useContext(BrandContext);
    let status = "success";
    let label = "";

    if (rate > 8) {
        label = "Excelente!";
    }
    if (rate <= 8) {
        label = "Muito Bom!";
    }
    if (rate <= 6) {
        status = "active";
        label = "Intermediário";
    }
    if (rate <= 4) {
        status = "error";
        label = "Ruim";
    }
    if (rate <= 2) {
        label = "Muito Ruim";
    }



    return (
        <Box className={classes.root}>
            <Progress
                type="circle"
                percent={10 * rate}
                status={status}
                width="100%"
                strokeWidth={4}
                theme={{
                    error: {
                        symbol: " ",
                        trailColor: "rgba(255, 51, 68, .4)",
                        color: ctx.theme.error,
                    },
                    default: {
                        symbol: " ",
                        trailColor: "rgba(255, 51, 68, .4)",
                        color: ctx.theme.error,
                    },
                    active: {
                        symbol: " ",
                        trailColor: "rgba(248, 219, 28, .4)",
                        color: ctx.theme.secondary,
                    },
                    success: {
                        symbol: " ",
                        trailColor: "rgba(103, 196, 83, .4)",
                        color: ctx.theme.success,
                    },
                }}
            />
            <Typography
                type="body2semibold"
                className={`${classes.rate} ${classes[status]}`}
            >
                {rate}
            </Typography>
            <Typography type="captionregular" className={classes.label}>
                {label}
            </Typography>
        </Box>
    );
};

Rate.propTypes = {
    classes: PropTypes.object.isRequired,
    rate: PropTypes.number.isRequired,
};

export default withStyles(styles, { withTheme: true })(Rate);
