/* eslint-disable quotes */
export const styles = theme => ({
    root: {
        border: "2px solid transparent",
        borderRadius: theme.spacing(2),
        boxShadow: theme.shadows[16],
        cursor: "pointer",
        overflow: "hidden",
        padding: theme.spacing(3) / 2,
        position: "relative",
        "& button": {
            display: "none"
        },
        "&:hover": {
            borderColor: theme.palette.spot ? theme.palette.hotel.main : theme.palette.primary.main,

            "& .title": {
                color: theme.palette.hotel.main
            },
            "& button": {
                display: "block"
            },
            "& .overlay:before": {
                background: theme.palette.gradient ? theme.palette.gradient.black.leftToRigth : "",
                borderRadius: theme.spacing(1),
                bottom: 0,
                content: "''",
                left: 0,
                position: "absolute",
                right: 0,
                top: 0
            }
        },
        [theme.breakpoints.between("xs", "sm")]: {
            border: "none",
            padding: `${theme.spacing(2)}px ${theme.spacing(2)}px ${theme.spacing(3) / 2}px ${theme.spacing(2)}px`,
        }
    },
    rootLoading: {
        boxShadow: theme.shadows[0],
        "&:hover": { borderColor: "transparent" }
    },
    isActive: {
        borderColor: theme.palette.spot ? theme.palette.hotel.main : theme.palette.primary.main
    },
    loading: {
        cursor: "default",
        height: "244px",
        padding: theme.spacing(1, 0)
    },
    relative: {
        borderRadius: theme.spacing(2),
        overflow: "hidden",
        position: "relative"
    },
    hotel: {
        "&:hover": {
            borderColor: theme.palette.hotel ? theme.palette.hotel.main : theme.palette.primary.main
        }
    },
    package: {
        "&:hover": {
            borderColor: theme.palette.package ? theme.palette.package.main : theme.palette.primary.main
        }
    },
    rootList: { padding: theme.spacing(3) / 2 },
    typeList: {
        display: "flex",
        alignItems: "center"
    },
    content: {
        display: "flex",
        flexDirection: "column",
        padding: `${theme.spacing(1)}px ${theme.spacing(1)}px ${theme.spacing(1)}px 20px`,
        position: "relative",
        width: "50%"
    },
    img: {
        display: "block",
        left: "50%",
        maxWidth: "110%",
        minHeight: "100%",
        minWidth: "100%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        top: "50%"
    },
    arrows: {
        background: "none",
        border: "none",
        color: theme.palette.white.main,
        cursor: "pointer",
        outline: "none",
        padding: 0,
        position: "absolute",
        transform: "translateY(-50%)",
        top: "50%",
        zIndex: theme.level.content
    },
    arrowLeft: {
        left: theme.spacing(2)
    },
    arrowRight: {
        right: theme.spacing(2)
    },
    mealIcon: {
        boxSizing: "border-box",
        padding: "4px"
    },
    headerTitle: {
        display: "flex",
        justifyContent: "space-between",
    },
    title: {
        display: "block",
        overflow: "hidden",
        textOverflow: "ellipsis",
        whiteSpace: "nowrap",
        width: "65%",
        [theme.breakpoints.between("xs", "sm")]: {
            marginTop: theme.spacing(3),
            width: "100%"
        }
    },
    footer: {
        borderTop: `1px solid ${theme.palette.grey[50]}`,
        padding: `${theme.spacing(2)}px 0 0 0`,
        textAlign: "center",
        [theme.breakpoints.between("xs", "sm")]: {
            padding: `${theme.spacing(3) / 2}px 0 0 0`,
        }
    },
    footerItem: {
        "& + &": {
            marginLeft: theme.spacing(1) / 2,
            "&:before": {
                borderLeft: `1px solid ${theme.palette.text.primary}`,
                content: '""',
                display: "inline-block",
                height: "10px",
                marginRight: theme.spacing(1) / 2,
                verticalAlign: "middle"
            }
        }
    },
    priceContainer: {
        textAlign: "right",
        margin: `${theme.spacing(3)}px 0`
    },
    imgContainerCard: {
        display: "block",
        height: "185px",
        marginLeft: theme.spacing(2) * -1,
        marginTop: theme.spacing(2) * -1,
        position: "relative",
        width: `calc(100% + ${theme.spacing(4)}px)`,
        "& button": {
            display: "block"
        },
        "& .overlay:before": {
            background: theme.palette.gradient ? theme.palette.gradient.black.leftToRigth : "",
            borderRadius: theme.spacing(1),
            bottom: 0,
            content: "''",
            left: 0,
            position: "absolute",
            right: 0,
            top: 0
        },
        "& .slick-dots": {
            bottom: theme.spacing(5) / 2
        }
    },
    imgContainerList: {
        height: "224px",
        maxWidth: "50%",
        minWidth: "50%",
        position: "relative",
        width: "50%",
        "& .slick-dots": {
            bottom: theme.spacing(5) / 2
        }
    },
    imgWrapper: {
        background: theme.palette.backdrop ? theme.palette.backdrop.main : theme.palette.primary.main,
        display: "block",
        height: "185px",
        overflow: "hidden",
        position: "relative",
        borderRadius: `${theme.spacing(1)}px`,
        [theme.breakpoints.between("xs", "sm")]: {
            borderRadius: 0,
        }
    },
    imgWrapperList: {
        borderRadius: `${theme.spacing(1)}px`,
        "-webkit-border-radius": `${theme.spacing(1)}px`,
        height: "224px",

    },
    overlay: {
        background: theme.palette.gradient ? theme.palette.gradient.black.bottom : "",
        borderRadius: theme.spacing(1),
        "-webkit-border-radius": `${theme.spacing(1)}px`,
        bottom: 0,
        content: '""',
        left: 0,
        position: "absolute",
        right: 0,
        top: 0,
        zIndex: theme.level.content,
        [theme.breakpoints.between("xs", "sm")]: {
            borderRadius: 0,
            "-webkit-border-radius": 0,
        }
    },
    notAvaliableImg: { filter: "grayscale(1)" },
    subtitle: {
        display: "block",
        margin: `${theme.spacing(1) / 2}px 0 ${theme.spacing(3) / 2}px 0`,
    },
    loadingContent: { marginTop: theme.spacing(2) },
    loadingPrice: { marginTop: theme.spacing(5) },
    topTag: {
        left: theme.spacing(3),
        position: "absolute",
        top: theme.spacing(3),
        zIndex: theme.level.content + 1,
        fontWeight: "bold",
        [theme.breakpoints.between("xs", "sm")]: {
            left: theme.spacing(2),
            top: theme.spacing(2),
        }
    },
    favorite: {
        color: theme.palette.white ? theme.palette.white.main : theme.palette.primary.main,
        cursor: "pointer",
        position: "absolute",
        right: theme.spacing(2),
        top: theme.spacing(2)
    },
    verMapa: {
        color: theme.palette.primary.main,
        fontWeight: "bold",
        cursor: "pointer",
        "&:hover": {
            textDecoration: "underline"
        }
    },
    favoriteList: { color: theme.palette.text.primary },
    stars: {
        color: theme.palette.secondary.main,
        marginRight: theme.spacing(0.5)
    },
    tripAdvisor: {
        margin: `${theme.spacing(2)}px 0`,
        width: "100%"
    },
    tripAdvisorList: {
        margin: `${theme.spacing(2)}px 0`,
        minHeight: "36px",
        width: "50%"
    },
    lastItem: {
        padding: `0 ${theme.spacing(1) / 2}px`,
        fontWeight: "bold",
    },
    discountCard: {
        position: "absolute",
        right: "16px",
        top: "155px",
        zIndex: theme.level.content,
        margin: `${theme.spacing(2)}px 0`,
    },
    discountList: {
        margin: `${theme.spacing(2)}px 0`,
        width: "70px", 
        minHeight: "36px",
    },
    info: {
        alignItems: "center",
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between"
    },
    flight: {
        display: "flex",
        flexDirection: "column"
    },
    flightInfo: {
        alignItems: "center",
        display: "flex",
        justifyContent: "space-between",
        width: "150px"
    },
    luggage: { color: theme.palette.primary.main }
});
