export const styles = theme => ({
    root: {
        [theme.breakpoints.between("xs", "md")]: {
            borderRadius: "16px 16px 0 0",
            bottom: 0,
            left: 0,
            position: "fixed",
            right: 0,
            transition: "all ease .5s",
            zIndex: theme.level.fixed,
        },
    },
    overlay: {
        zIndex: theme.level.negative,
    },
    closed: {
        [theme.breakpoints.between("xs", "md")]: {
            paddingBottom: 0,
        },
    },
    closedContent: {
        height: 0,
        transition: "all ease .5s",
        overflow: "hidden",
    },
    openedContent: {
        height: "270px",
        transition: "all ease .5s",
    },
    header: {
        marginBottom: theme.spacing(2),
    },
    icon: {
        float: "right",
    },
    iconActive: {
        transform: "rotate(180deg)",
        transition: "all ease .5s",
    },
});
