import arrowLeft from "../assets/img/ic-drop-left.svg";
import arrowRight from "../assets/img/ic-drop-right.svg";

export const styles = theme => ({
    root: props => ({
        height: "100%",
        position: "relative",
        "& .DayPicker__firstDayUnlocked": {
            borderTop: "1px solid #333333",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            margin: "0px 12px"
        },
        "& .DayPicker__firstDayUnlockedMobileContainer": {
            display: "flex",
            flexGrow: 1,
            justifyContent: "center",
            alignItems: "center"
        },
        "& .DayPicker__firstDayUnlockedMobile": {
            borderTop: "1px solid #333333"
        },
        "& .DayPicker": {
            counterReset: "trecho",
            fontFamily: theme.typography.fontFamily,
            position: "relative",
            margin: "auto"
        },
        "& .DayPicker__horizontal": {
            background: "transparent",
            boxShadow: "none"
        },
        "& .DayPicker__withBorder": {
            boxShadow: "none"
        },
        "& .DayPickerNavigation__verticalScrollableDefault": {
            display: "none"
        },
        "& .DateRangePicker": {},
        "& .DateRangePicker strong": {
            fontWeight: "normal"
        },
        "& .DateRangePicker b": {
            fontWeight: "normal"
        },
        "& .DateRangePickerInput": {
            border: "none",
            display: "flex"
        },
        "& .DateRangePickerInput_arrow:before": {
            borderBottom: "solid 1px $color-gray-dark",
            content: "",
            display: "block",
            margin: "13px 15px 0",
            opacity: 0.5,
            width: "15px"
        },
        "& .DateRangePickerInput_arrow_svg": {
            display: "none"
        },
        "& ._picker": {
            borderRadius: "8px"
        },
        "& ._picker__portal": {
            zIndex: theme.level.modal
        },
        "& .DateInput": {
            background: "none",
            width: "auto"
        },
        "& .DateInput_input": {
            color: theme.palette.grey[50],
            cursor: "pointer",
            fontFamily: "HindMRegular",
            // font-size: $font-size-highlight;
            padding: 0,
            width: "83px"
        },
        "& .DateInput_input[value='']": {
            width: "58px"
        },
        "& .DateInput_input__focused": {
            borderColor: theme.palette.primary.main
        },
        "& .DateInput_fang": {
            top: "53px !important"
        },
        "& .CalendarMonth_caption": {
            // @extend %font-medium,
            // color: $color-gray-dark,
            fontFamily: theme.typography.fontFamily,
            fontSize: "14px",
            paddingLeft: "10px",
            // textAlign: "left",
            textTransform: "capitalize"
        },

        "& .CalendarMonthGrid__horizontal": {
            top: props.isMobile ? "unset" : "-15px"
        },
        "& .DayPicker_transitionContainer": {
            height: props.isMobile ? "unset" : "305px !important"
        },
        "& .DayPicker_weekHeaders": {
            top: props.isMobile ? "unset" : "-25px"
        },
        "& .DayPicker_weekHeader": {
            boxShadow: props.isMobile ? "0px 9px 11px 0px rgba(0, 0, 0, 0.08) !important" : "unset",
            borderTop: props.isMobile ? "1px solid #DDDDDD" : "unset"
        },
        "& .DayPicker_weekHeader_li": {
            color: "#999999",
            textTransform: "uppercase",
            fontWeight: "bold"
        },
        "& .DayPicker_weekHeader__verticalScrollable": {
            padding: "0 !important"
        },
        "& .DayPicker_weekHeader__verticalScrollable .DayPicker_weekHeader_ul": {
            display: "flex",
            justifyContent: "space-around"
        },
        "& .DayPicker_weekHeader_ul": {
            padding: props.isMobile ? "10px" : "unset"
        },
        "& .CalendarDay": {
            fontSize: "12px"
        },
        "& .CalendarDay__blocked_out_of_range": {
            background: "transparent",
            border: "none",
            position: "relative",
            zIndex: theme.level.base
        },
        "& .CalendarDay__blocked_out_of_range:active": {
            background: "transparent",
            border: "none",
            position: "relative",
            zIndex: theme.level.base
        },
        "& .CalendarDay__blocked_out_of_range:hover": {
            [theme.breakpoints.between("md", "xl")]: {
                background: "transparent",
                border: "none",
                position: "relative",
                zIndex: theme.level.base
            }
        },
        "& .CalendarDay__default:not(.CalendarDay__blocked_out_of_range):hover": {
            [theme.breakpoints.between("md", "xl")]: {
                border: "none",
                position: "relative",
                color: theme.palette.white.main,
                zIndex: theme.level.base,
                background: theme.palette.primary.main,
                borderRadius: "100%"
            }
        },
        "& .CalendarDay__default.CalendarDay__selected_span:hover": {
            [theme.breakpoints.between("md", "xl")]: {
                background: theme.palette.primary.alpha,
                borderRadius: 0,
                color: theme.palette.text.primary
            }
        },
        "& .CalendarDay__default": {
            background: "transparent",
            border: "none",
            position: "relative",
            zIndex: theme.level.base // $z-index-base
        },
        "& .CalendarDay__selected": {},
        "& .CalendarDay__selected:active:hover": {
            background: "transparent"
        },
        "& .CalendarDay__selected:after": {
            background: theme.palette.primary.main,
            borderRadius: "100%",
            content: '""',
            height: "40px",
            position: "absolute",
            width: "40px",
            zIndex: theme.level.negative,
            left: "50%",
            top: "50%",
            transform: "translate(-50%, -50%)"
        },
        "& .CalendarDay__selected:active:after": {
            background: theme.palette.primary.main,
            borderRadius: "100%",
            content: "",
            height: "40px",
            position: "absolute",
            width: "40px",
            zIndex: theme.level.negative,
            left: "50%",
            top: "50%",
            transform: "translate(-50%, -50%)"
        },
        "& .CalendarDay__selected:hover:after": {
            background: theme.palette.primary.main,
            borderRadius: "100%",
            content: "",
            height: "40px",
            position: "absolute",
            width: "40px",
            zIndex: theme.level.negative,
            left: "50%",
            top: "50%",
            transform: "translate(-50%, -50%)"
        },
        // "& .CalendarDay__selected:_start": {},
        "& .CalendarDay__selected_start:not(.CalendarDay__blocked_minimum_night):before": {
            background: `linear-gradient(to right, ${theme.palette.white.main}, ${theme.palette.primary.alpha})`, // background-image: linear-gradient(to right, $color-white, ${theme.palette.primary.alpha});
            content: '""',
            display: "block",
            height: "100%",
            position: "absolute",
            right: 0,
            top: 0,
            width: "100%",
            zIndex: theme.level.negative
        },

        "& .CalendarDay__defaultCursor:before": {
            display: "none"
        },
        "& .CalendarDay__selected_span": {
            background: theme.palette.primary.alpha,
            color: theme.palette.text.primary
        },
        "& .CalendarDay__selected_span.CalendarDay__lastDayOfWeek": {
            background: `linear-gradient(to right, ${theme.palette.primary.alpha}, rgba(35, 127, 208, 0))`
        },
        "& .CalendarDay__selected_span.CalendarDay__firstDayOfWeek": {
            background: `linear-gradient(to left, ${theme.palette.primary.alpha}, rgba(35, 127, 208, 0))`
        },
        "& .CalendarDay__selected_span:hover": {
            // background: ${theme.palette.primary.alpha};
            // color: $color-black;
        },
        "& .CalendarDay__selected_end": {
            borderRadius: "100%"
        },
        "& .CalendarDay__selected_end:before": {
            background: `${theme.palette.primary.alpha}`, // lighten(theme.palette.primary.main, 20),
            content: "''",
            display: "block",
            height: "100%",
            position: "absolute",
            left: 0,
            top: 0,
            width: "50%",
            zIndex: theme.level.negative
        },
        "& .CalendarDay__selected__hovered_span": {},
        "& .CalendarDay__selected__hovered_span:hover": {
            background: theme.palette.primary.main, // ${theme.palette.primary.alpha};
            color: theme.palette.text.primary // $color-black;
        },
        "& .CalendarDay__selected__hovered_span ~  .CalendarDay__selected__defaultCursor:before": {
            display: "block"
        },
        "& .CalendarDay__highlighted_calendar": {
            color: theme.palette.spot.main
        },
        "& .CalendarDay__highlighted_calendar:after": {
            counterIncrement: "trecho",
            color: theme.palette.spot.main,
            display: "block",
            fontSize: "10px",
            content: "\"Trecho \" counter(trecho)" // prettier-ignore
        },
        "& .CalendarDay__selected__highlighted_calendar": {
            color: theme.palette.spot.main
        },
        "& .CalendarDay__selected__highlighted_calendar:after": {
            color: theme.palette.spot.main,
            content: "Trecho"
        },

        "& .CalendarMonthGrid__vertical_scrollable": {
            width: "100% !important",
            marginTop: props.isMobile ? "28px" : "unset"
        },

        "& .CalendarMonth_table": {
            width: "100%"
        },

        "& .DayPickerKeyboardShortcuts_buttonReset": {
            display: "none"
        },

        "& .DayPickerNavigation": {
            // left: "-20px",
            // position: "absolute",
            // right: "-20px",
            // top: "50%",
            transform: "translateY(-50%)"
        },

        "& .DayPickerNavigation_nextButton__verticalDefault": {
            display: "none"
        },

        "& .DayPickerNavigation_button__horizontalDefault": {
            background: `${theme.palette.white.main} no-repeat center`,
            border: "none !important",
            borderRadius: "16px",
            // boxShadow: "0px 0px 4px rgba(0, 0, 0, 0.102)",
            padding: 0,
            height: "40px",
            top: "-5px",
            width: "40px"
        },
        "& .DayPickerNavigation_button__horizontalDefault svg": {
            display: "none"
        },
        "& .DayPickerNavigation_leftButton__horizontalDefault": {
            backgroundImage: `url(${arrowLeft})`,
            left: 0
        },
        "& .DayPickerNavigation_leftButton__horizontalDefault:active": {
            backgroundImage: `url(${arrowLeft})`,
            backgroundRepeat: "no-repeat",
            // backgroundPosition: "center",
            outline: "none"
        },
        "& .DayPickerNavigation_rightButton__horizontalDefault": {
            backgroundImage: `url(${arrowRight})`,
            right: 0,
            outline: "none"
        },
        "& .DayPickerNavigation_rightButton__horizontalDefault:active": {
            backgroundImage: `url(${arrowRight})`,
            backgroundRepeat: "no-repeat",
            backgroundPosition: "center",
            outline: "none"
        }
    })
});
