import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Meal from "@cvccorp-components/icons/dist/Meal";
import FilterChip from "../FilterChip";
import Tag from "../Tag";
import Typography from "../Typography";
import Button from "../Button";
import LoadingContent from "../LoadingContent";
import Box from "../Box";
import { styles } from "./styles";

const Matrix = ({ classes, cias, isLoading }) => {
    return (
        <Box>
            <Typography type="body1semibold">Melhor pre√ßo por companhia</Typography>
            {cias.map((item, i) => (
                <Grid className={classes.item} container spacing={1} key={i}>
                    <Grid item xs={12}>
                        {isLoading ? (
                            <Grid container>
                                <Grid item xs={6}>
                                    <LoadingContent className={classes.imgLoading} />
                                    <LoadingContent className={classes.nameLoading} />
                                </Grid>
                            </Grid>
                        ) : (
                            <>
                                <img className={classes.img} src={item.img} alt={item.name} />
                                <Typography className={classes.name} type="body2semibold">
                                    {item.name}
                                </Typography>
                            </>
                        )}
                    </Grid>
                    <Grid item xs={4}>
                        <FilterChip
                            matrix
                            disabled={!item.directFlight}
                            label={
                                <span>
                                    {isLoading ? (
                                        <>
                                            <LoadingContent className={classes.labelLoading} />
                                            <LoadingContent className={classes.priceLoading} />
                                        </>
                                    ) : (
                                        <>
                      Voo direto
                                            <strong className={classes.matrixPrice}>
                                                <Typography
                                                    type="body2semibold"
                                                    className={
                                                        item.directFlight &&
                            item.directFlight.isBestOfCategory &&
                            classes.matrixBestPrice
                                                    }
                                                >
                                                    {item.directFlight
                                                        ? `R$ ${item.directFlight.price}`
                                                        : "R$ 0"}
                                                </Typography>
                                            </strong>
                                            {item.directFlight &&
                        item.directFlight.isBestOfGeneral && (
                                                <Tag
                                                    label="Melhor"
                                                    className={classes.matrixBestPriceTag}
                                                    icon={
                                                        <Meal className={classes.matrixBestPriceIcon} />
                                                    }
                                                />
                                            )}
                                        </>
                                    )}
                                </span>
                            }
                        />
                    </Grid>
                    <Grid item xs={4}>
                        <FilterChip
                            matrix
                            disabled={!item.oneStopFlight}
                            label={
                                <span>
                                    {isLoading ? (
                                        <>
                                            <LoadingContent className={classes.labelLoading} />
                                            <LoadingContent className={classes.priceLoading} />
                                        </>
                                    ) : (
                                        <>
                      1 parada
                                            <strong className={classes.matrixPrice}>
                                                <Typography
                                                    type="body2semibold"
                                                    className={
                                                        item.oneStopFlight &&
                            item.oneStopFlight.isBestOfCategory &&
                            classes.matrixBestPrice
                                                    }
                                                >
                                                    {item.oneStopFlight
                                                        ? `R$ ${item.oneStopFlight.price}`
                                                        : "R$ 0"}
                                                </Typography>
                                            </strong>
                                            {item.oneStopFlight &&
                        item.oneStopFlight.isBestOfGeneral && (
                                                <Tag
                                                    label="Melhor"
                                                    className={classes.matrixBestPriceTag}
                                                    icon={
                                                        <Meal className={classes.matrixBestPriceIcon} />
                                                    }
                                                />
                                            )}
                                        </>
                                    )}
                                </span>
                            }
                        />
                    </Grid>
                    <Grid item xs={4}>
                        <FilterChip
                            matrix
                            disabled={!item.moreStopFlight}
                            label={
                                <span>
                                    {isLoading ? (
                                        <>
                                            <LoadingContent className={classes.labelLoading} />
                                            <LoadingContent className={classes.priceLoading} />
                                        </>
                                    ) : (
                                        <>
                      2/+ paradas
                                            <strong className={classes.matrixPrice}>
                                                <Typography
                                                    type="body2semibold"
                                                    className={
                                                        item.moreStopFlight &&
                            item.moreStopFlight.isBestOfCategory &&
                            classes.matrixBestPrice
                                                    }
                                                >
                                                    {item.moreStopFlight
                                                        ? `R$ ${item.moreStopFlight.price}`
                                                        : "R$ 0"}
                                                </Typography>
                                            </strong>
                                            {item.moreStopFlight &&
                        item.moreStopFlight.isBestOfGeneral && (
                                                <Tag
                                                    label="Melhor"
                                                    className={classes.matrixBestPriceTag}
                                                    icon={
                                                        <Meal className={classes.matrixBestPriceIcon} />
                                                    }
                                                />
                                            )}
                                        </>
                                    )}
                                </span>
                            }
                        />
                    </Grid>
                </Grid>
            ))}
            <Button
                className={classes.moreBtn}
                color="primary"
                outline
                label="Ver mais companhias"
                disabled={isLoading}
                variant="text"
            />
        </Box>
    );
};

Matrix.propTypes = {
    classes: PropTypes.object.isRequired,
    cias: PropTypes.array.isRequired,
    isLoading: PropTypes.bool,
};

export default withStyles(styles, { withTheme: true })(Matrix);
