const styles = theme => ({
    root: {
        border: "2px solid transparent",
        cursor: "pointer",
        overflow: "hidden",
        padding: theme.spacing(2),
    },
    imgContainer: {
        height: "48px",
        overflow: "hidden",
        position: "relative",
        width: "48px",
    },
    thumb: {
        display: "block",
        left: "50%",
        position: "absolute",
        transform: "translate(-50%, -50%)",
        top: "50%",
    },
    title: {
        fontWeight: "normal",
        lineHeight: 0,
        margin: 0,
        "& + &": { marginTop: theme.spacing(0.5) },
    },
    detail: {
        alignItems: "center",
        display: "flex",
        justifyContent: "space-between",
    },
    detailBtn: {
        color: theme.palette.primary.main,
        height: "auto",
        minWidth: 0,
        padding: 0,
    },
    infoContainer: {
        display: "block",
        margin: `${theme.spacing(2)}px 0`,
    },
    footer: {
        borderTop: `1px solid ${theme.palette.grey[50]}`,
        margin: `${theme.spacing(2)}px ${theme.spacing(2) * -1}px 0`,
        paddingTop: theme.spacing(2),
        textAlign: "center",
    },
});
