import React, { useState } from "react";
import PropTypes from "prop-types";
import DayPickerSingleDateController from "react-dates/lib/components/DayPickerSingleDateController";
import { withStyles } from "@material-ui/core/styles";
import isSameDay from "react-dates/lib/utils/isSameDay";
import moment from "moment";
import { styles } from "../styles";

const Calendar = ({
    onDateChange,
    isMobile,
    selectedDate,
    classes,
    isOutsideRange,
}) => {
    const [date, setDate] = useState(null);
    const [focusedInput, setFocusedInput] = useState("startDate");

    return (
        <div className={classes.root}>
            <DayPickerSingleDateController
                date={date}
                onDateChange={date => {
                    onDateChange(date);
                    setDate(date);
                }}
                focused={!!focusedInput}
                onFocusChange={({ focusedInput }) => setFocusedInput(focusedInput)}
                numberOfMonths={isMobile ? 12 : 2}
                isDayHighlighted={day1 =>
                    selectedDate &&
          selectedDate !== undefined &&
          !!selectedDate.length &&
          selectedDate.some(day2 => isSameDay(day1, day2))
                }
                orientation={isMobile ? "verticalScrollable" : "horizontal"}
                isOutsideRange={day =>
                    day.isBefore(moment("2020-06-01")) ||
          (isOutsideRange && isOutsideRange(day))
                }
            />
        </div>
    );
};
Calendar.propTypes = {
    onDateChange: PropTypes.func,
    isMobile: PropTypes.bool,
    selectedDate: PropTypes.arrayOf(PropTypes.object),
    isOutsideRange: PropTypes.func,
};
export default withStyles(styles, { withTheme: true })(Calendar);
