import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";

import ListItem from "@material-ui/core/ListItem";
import InputBase from "@material-ui/core/InputBase";
import List from "@material-ui/core/List";

import Air from "@cvccorp-components/icons/dist/Air";
import Hotel from "@cvccorp-components/icons/dist/Hotel";
import Local from "@cvccorp-components/icons/dist/Local";
import Clock from "@cvccorp-components/icons/dist/Clock";
import ArrowLeft from "@cvccorp-components/icons/dist/ArrowLeft";

import Typography from "../../Typography";
import Box from "../../Box";
import TextField from "../../TextField";
import Overlay from "../../Overlay";
import Animate from "../../Animate";
import { styles } from "../styles";
import CloseIcon from "@material-ui/icons/Close";

const PesqincHotel = ({
    classes,
    onChange,
    onFocus,
    onClose,
    onSelect,
    value,
    selectedIndex,
    transparent,
    search,
    isMobile,
    error,
    errorMsg,
    history,
    mostSearched,
    resetValue,
}) => {
    const [index, setIndex] = useState(selectedIndex || 0);
    const [searching, setSearching] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        setLoaded(true);
    }, []);

    const handleSelect = (item, index) => {
        setSearching(false);
        setSelectedItem(item);
        onSelect(item, index);
    };

    const handleKeyUp = e => {
        if (search && search.length > 0) {
            if (e.keyCode === 40) setIndex((index + 1) % search.length);
            else if (e.keyCode === 38)
                setIndex((index - 1 + search.length) % search.length);
            else if (e.keyCode === 13 && searching) {
                onSelect(search[index]);
                setSearching(false);
            }
        }
    };

    const handleChange = e => {
        if (onChange) onChange(e);
    };

    const handleFocus = e => {
        setSearching(true);
        if (onFocus) onFocus(e);
    };

    const handleClose = () => {
        if (window !== undefined && isMobile) {
            window.scrollTo(0, 0);
        }
        if (!selectedItem || value !== selectedItem.name) {
            setSelectedItem(null);
        }

        setSearching(false);
        if (onClose) onClose(selectedItem);
    };

    const [isOpenAnimation, setOpenAnimation] = useState(false);

    const onOpenAnimate = () => {
        setOpenAnimation(true);
        document.getElementById("pesqinc-input").focus();
    };
    const onCloseAnimate = () => {
        setOpenAnimation(false);
    };

    const handleReset = () => {
        resetValue();
    };

    return (
        <div className={classes.container}>
            {loaded && (
                <TextField
                    label="Para onde vai?"
                    size="large"
                    transparent={transparent}
                    value={value}
                    onClick={handleFocus}
                    onChange={handleChange}
                    onKeyUp={handleKeyUp}
                    inputClassName={classes.input}
                    labelClassName={classes.input}
                    error={error}
                    errorMsg={errorMsg}
                    icon={<Local />}
                    disabled
                    noBorder
                />
            )}

            <Animate
                isOpen={searching}
                onOpen={onOpenAnimate}
                onClose={onCloseAnimate}
            >
                {handleClose && <Overlay onClick={handleClose} />}

                <Box
                    className={`${classes.root} ${!isOpenAnimation && classes.closed}`}
                >
                    <div className={classes.internalInput}>
                        {isMobile ? (
                            <a onClick={handleClose}>
                                <ArrowLeft />
                            </a>
                        ) : (
                            <Local />
                        )}
                        <InputBase
                            placeholder="Para onde vai?"
                            value={value}
                            onFocus={handleFocus}
                            onChange={handleChange}
                            onKeyUp={e => handleKeyUp(e, value)}
                            className={classes.flexGrow}
                            id="pesqinc-input"
                        />
                        <CloseIcon
                            onClick={handleReset}
                        />
                    </div>
                    <div className={classes.boxResult}>
                        {!history && (!search || search.length === 0) && !mostSearched && (
                            <Typography
                                type="captionsemibold"
                                className={classes.listItemTitle}
                            >
                                Continue digitando…
                            </Typography>
                        )}
                        <List component="nav" className={classes.list}>
                            {search && search.length > 0 && (
                                <li className={classes.listItemTitle}>
                                    <Typography type="captionsemibold">
                                        Você está buscando por…
                                    </Typography>
                                </li>
                            )}
                            <div
                                className={`${!isMobile && classes.heightLimit} ${
                                    classes.scrolling
                                }`}
                            >
                                {search &&
                                    search.map((item, i) => (
                                        <ListItem
                                            key={i}
                                            className={classes.listItem}
                                            button
                                            selected={i === index}
                                            onClick={() => handleSelect(item, i)}
                                        >
                                            {item.type === "hotel" && <Hotel size="small" />}
                                            {item.type === "location" && <Local size="small" />}
                                            {item.type === "airport" && <Air size="small" />}
                                            <span className={classes.content}>
                                                <Typography
                                                    className={classes.name}
                                                    type="body2semibold"
                                                >
                                                    {item.name}
                                                </Typography>
                                                <Typography
                                                    className={classes.adressHotel}
                                                    type="body2regular"
                                                >
                                                    {" - "}
                                                    {item.address}
                                                </Typography>
                                            </span>
                                        </ListItem>
                                    ))}
                            </div>
                            {history && !(search && search.length > 0) && (
                                <li className={classes.listItemTitle}>
                                    <Typography type="captionsemibold">Últimas buscas</Typography>
                                </li>
                            )}
                            {history &&
                                !(search && search.length > 0) &&
                                history.map((item, i) => (
                                    <ListItem
                                        key={i}
                                        className={classes.listItem}
                                        button
                                        onClick={() => handleSelect(item, i)}
                                    >
                                        <Clock size="small" />
                                        <span className={classes.content}>
                                            <Typography className={classes.name} type="body2semibold">
                                                {item.name}
                                            </Typography>
                                            <Typography className={classes.date} type="body2regular">
                                                - {item.departure} à {item.arrival}
                                            </Typography>
                                        </span>
                                    </ListItem>
                                ))}
                            {mostSearched && !(search && search.length > 0) && (
                                <li className={classes.listItemTitle}>
                                    <Typography type="captionsemibold">
                                        Destinos mais buscados
                                    </Typography>
                                </li>
                            )}
                            {mostSearched &&
                                !(search && search.length > 0) &&
                                mostSearched.map((item, i) => (
                                    <ListItem
                                        key={i}
                                        className={classes.listItem}
                                        button
                                        onClick={() => handleSelect(item, i)}
                                    >
                                        {item.type === "hotel" && <Hotel size="small" />}
                                        {(item.type === "location" || !item.type) && (
                                            <Local size="small" />
                                        )}
                                        {item.type === "airport" && <Air size="small" />}
                                        <span className={classes.content}>
                                            <Typography className={classes.name} type="body2semibold">
                                                {item.name}
                                            </Typography>
                                            <Typography className={classes.date} type="body2regular">
                                                - diárias a partir de R$ {item.price}
                                            </Typography>
                                        </span>
                                    </ListItem>
                                ))}
                        </List>
                    </div>
                </Box>
            </Animate>
        </div>
    );
};

PesqincHotel.propTypes = {
    classes: PropTypes.object.isRequired,
    search: PropTypes.array,
    history: PropTypes.array,
    mostSearched: PropTypes.array,
    onChange: PropTypes.func,
    onFocus: PropTypes.func,
    onClose: PropTypes.func,
    onSelect: PropTypes.func,
    value: PropTypes.array,
    selectedIndex: PropTypes.number,
    transparent: PropTypes.bool,
    isMobile: PropTypes.bool,
    error: PropTypes.bool,
    errorMsg: PropTypes.string,
};

export default withStyles(styles, { withTheme: true })(PesqincHotel);
