import React from "react";
import { Paper, Divider } from "@material-ui/core";
import { makeStyles  , MuiThemeProvider } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import { storiesOf   } from "@storybook/react";
import Avaliation from "../../Avaliation";
import themes from "../../Theme/themes";
import Tag from "../Tag";

const useStyles = makeStyles((theme) => ({
    root: {
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
    darkBg: {
        background: "rgba(0, 0, 0, 0.6)",
        boxShadow: theme.shadows[8],
        padding: "30px",
    },
}));

const TagExample = ({ brand, mode, label, examples, color, size }) => {
    const classes = useStyles(themes[brand]);
    const offers = ["20% Off", "20% Off", "Hotel", "Aéreo", "Aéreo + Hotel", "Cancelado", "Processado", "Aprovado"];
    const colors = ["spot", "white", "hotel", "air", "package", "canceled", "processed", "approved"]

    return (
        <MuiThemeProvider theme={themes[brand]}>
            <Paper className={classes[mode]} id="tags">
                <Typography variant="h3">Tags</Typography>
                <br />
                <Divider />
                <br />
                <Typography variant="subtitle1">Ofertas</Typography>
                <br />

                <Tag
                    label={examples === "custom" ? label : examples }
                    color={examples === "custom" ? color: colors[offers.indexOf(examples)] }
                />
                <br />
                <br />

                <Typography variant="subtitle1">Avaliações</Typography>
                <br />
                <Avaliation label="8,3" size={size} />
            </Paper>
        </MuiThemeProvider>
    );
};

storiesOf("Components/Tags", module).addParameters({ storySource: { source: __STORY__, locationsMap: __LOCATIONS_MAP__ } })
    .add("Default", (args) => <TagExample {...args}/>, {
        args: {
            brand: "CVC",
            mode: "root",
            label: "Tag",
            color: "spot",
            size: "small",
            examples: "custom",
        },
        argTypes: {
            brand: {
                type: "select",
                options: ["BlackAndWhite", "BlackFriday", "CVC", "CVCCorp", "CVCYellow", "Sub"]
            },
            mode: {
                type: "select",
                options: ["root", "darkBg"]
            },
            label: { type: "string" },
            color: {
                type: "select",
                options: ["spot", "white", "hotel", "air", "package", "canceled", "processed", "approved"]
            },
            size: {
                type: "select",
                options: ["small", "medium"]
            },
            examples: {
                type: "select",
                options: [
                    "20% Off",
                    "20% Off",
                    "Hotel",
                    "Aéreo",
                    "Aéreo + Hotel",
                    "Cancelado",
                    "Processado",
                    "Aprovado",
                    "custom",
                ]
            },
        }
    })
