import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import FormHelperText from "@material-ui/core/FormHelperText";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import MenuItem from "@material-ui/core/MenuItem";
import { styles } from "./styles";

const CustomSelect = ({
    classes,
    disabled,
    icon,
    size,
    noBorder,
    transparent,
    searchOrigin,
    searchDestiny,
    select,
    id,
    label,
    onChange,
    onFocus,
    onBlur,
    onKeyUp,
    error,
    value,
    children,
    errorMsg,
    customClass,
}) => {
    const formControl = `
        ${classes.formControl}
        ${disabled ? classes.disabledLabel : ""}
        ${transparent && classes.transparentRoot}
        ${transparent && noBorder && classes.transparentRootNoBorder}
    `;

    const lbClasses = `
        ${size === "large" ? classes.labelLarge : classes.label}
        ${disabled && classes.disabledLabel}
        ${icon && size === "large" && noBorder && classes.lbIconLargeNoBorder}
        ${icon && size === "large" && classes.lbIconLarge}
        ${icon && classes.lbIcon}
        ${transparent && classes.lbtransparent}
        ${searchOrigin && classes.labelSearchOrigin}
        ${searchDestiny && classes.labelSearchDestiny}
        `;
    const inputClasses = `
        ${size === "large" ? classes.large : classes.root}
        ${disabled ? classes.disabled : ""}
        ${icon && select && classes.selectIcon}
        ${icon && !select && !size === "large" && classes.inputIcon}
        ${icon && !select && size === "large" && classes.inputIconLarge}
        ${noBorder && classes.noBorder}
        ${transparent && classes.transparent}
        ${noBorder && transparent && classes.noBorderTransparent}
        ${classes.flexContent}
    `;
    const iconClasses = `
        ${classes.icon}
        ${noBorder ? classes.iconNoBorder : ""}
        ${transparent ? classes.lbtransparent : ""}
        ${error ? classes.iconError : ""}
    `;

    return (
        <FormControl className={formControl}>
            <span className={iconClasses}>{icon}</span>
            <TextField
                id={id}
                label={label}
                variant="outlined"
                inputProps={{
                    className: customClass ? `${customClass} ${inputClasses}` : inputClasses 
                }}
                InputLabelProps={{ className: lbClasses }}
                onChange={onChange}
                onFocus={onFocus}
                onBlur={onBlur}
                onKeyUp={onKeyUp}
                error={error}
                disabled={disabled}
                value={value || ""}
                SelectProps={{ IconComponent: KeyboardArrowDownIcon }}

            >
            </TextField>
            {error && errorMsg && <FormHelperText className={classes.errorText}>{errorMsg}</FormHelperText>}{" "}
        </FormControl>
    );
};

CustomSelect.propTypes = {
    classes: PropTypes.object.isRequired,
    size: PropTypes.string,
    disabled: PropTypes.bool,
    icon: PropTypes.object,
    transparent: PropTypes.bool,
    searchOrigin: PropTypes.bool,
    searchDestiny: PropTypes.bool,
    noBorder: PropTypes.bool,
    select: PropTypes.array,
    id: PropTypes.string,
    label: PropTypes.string,
    error: PropTypes.bool,
    errorMsg: PropTypes.string
};

export default withStyles(styles, { withTheme: true })(CustomSelect);
