import React from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import Grid from "@material-ui/core/Grid";
import Meal from "@cvccorp-components/icons/dist/Meal";
import Slider from "react-slick";
import Tag from "../Tag";
import Typography from "../Typography";
import Button from "../Button";
import LoadingContent from "../LoadingContent";
import Box from "../Box";
import { styles } from "./styles";

const Matrix = ({ classes, cias, isLoading, handleClick }) => {
    return (
        <Box>
            <Grid container alignItems="flex-start">
                <Grid item xs={2} direction="column">
                    <Grid item xs={12}>
                        <>
                            <Typography className={classes.flightHeader} type="body1semibold">
                                Melhor preço por companhia
                            </Typography>
                        </>
                    </Grid>
                    <Grid className={classes.highlightTitleStops} item xs={12}>
                        <Button
                            className={classes.flightHighlight}
                            label={
                                <span>
                                    <>
                                        <Typography type="body2semibold">Voo direto</Typography>
                                    </>
                                </span>
                            }
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <Button
                            className={classes.flightHighlight}
                            label={
                                <span>
                                    <>
                                        <Typography type="body2semibold">1 parada</Typography>
                                    </>
                                </span>
                            }
                        />
                    </Grid>
                    <Grid className={classes.highlightTitleStops} item xs={12}>
                        <Button
                            className={classes.flightHighlight}
                            label={
                                <span>
                                    <>
                                        <Typography type="body2semibold">2/+ paradas</Typography>
                                    </>
                                </span>
                            }
                        />
                    </Grid>
                </Grid>
                <div className={classes.slider}>
                    <Slider infinite={false} speed={500} slidesToShow={5} slidesToScroll={1} touchThreshold={100} arrows dots={false}>
                        {cias.map((item, i) => (
                            <Grid className={classes.columnLine} item xs={2} direction="column" alignItems="flex-start" key={i}>
                                <Grid className={classes.flightHeader} item xs={12}>
                                    {isLoading ? (
                                        <Grid>
                                            <Grid>
                                                <LoadingContent className={classes.imgLoading} />
                                                <LoadingContent className={classes.nameLoading} />
                                            </Grid>
                                        </Grid>
                                    ) : (
                                        <>
                                            <img className={classes.img} src={`https://almundo-com-res.cloudinary.com/image/upload/global/logos/digital-air-companies/${item.ciaCode}.svg`} alt={item.name} />
                                            <Typography className={classes.name} type="body2semibold">
                                                {item.name}
                                            </Typography>
                                        </>
                                    )}
                                </Grid>
                                <Grid className={classes.highlightCell} item xs={12}>
                                    <Button
                                        className={classes.flightHighlight}
                                        onClick={() => handleClick(item.directFlight)}
                                        matrix
                                        disabled={!item.directFlight}
                                        label={
                                            <span>
                                                {isLoading ? (
                                                    <>
                                                        <LoadingContent className={classes.labelLoading} />
                                                        <LoadingContent className={classes.priceLoading} />
                                                    </>
                                                ) : (
                                                    <>
                                                        <strong className={classes.matrixPrice}>
                                                            <Typography type="body2semibold" className={item.directFlight && item.directFlight.isBestOfCategory && classes.matrixBestPrice}>
                                                                {item.directFlight ? `R$ ${item.directFlight.price}` : "-"}
                                                            </Typography>
                                                        </strong>
                                                        {item.directFlight && item.directFlight.isBestOfGeneral && <Tag label="Melhor" className={classes.matrixBestPriceTag} icon={<Meal className={classes.matrixBestPriceIcon} />} />}
                                                    </>
                                                )}
                                            </span>
                                        }
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <Button
                                        className={classes.flightHighlight}
                                        onClick={() => handleClick(item.oneStopFlight)}
                                        matrix
                                        disabled={!item.oneStopFlight}
                                        label={
                                            <span>
                                                {isLoading ? (
                                                    <>
                                                        <LoadingContent className={classes.labelLoading} />
                                                        <LoadingContent className={classes.priceLoading} />
                                                    </>
                                                ) : (
                                                    <>
                                                        <strong className={classes.matrixPrice}>
                                                            <Typography type="body2semibold" className={item.oneStopFlight && item.oneStopFlight.isBestOfCategory && classes.matrixBestPrice}>
                                                                {item.oneStopFlight ? `R$ ${item.oneStopFlight.price}` : "-"}
                                                            </Typography>
                                                        </strong>
                                                        {item.oneStopFlight && item.oneStopFlight.isBestOfGeneral && <Tag label="Melhor" className={classes.matrixBestPriceTag} icon={<Meal className={classes.matrixBestPriceIcon} />} />}
                                                    </>
                                                )}
                                            </span>
                                        }
                                    />
                                </Grid>
                                <Grid className={classes.highlightCell} item xs={12}>
                                    <Button
                                        className={classes.flightHighlight}
                                        onClick={() => handleClick(item.moreStopFlight)}
                                        matrix
                                        disabled={!item.moreStopFlight}
                                        label={
                                            <span>
                                                {isLoading ? (
                                                    <>
                                                        <LoadingContent className={classes.labelLoading} />
                                                        <LoadingContent className={classes.priceLoading} />
                                                    </>
                                                ) : (
                                                    <>
                                                        <strong className={classes.matrixPrice}>
                                                            <Typography type="body2semibold" className={item.moreStopFlight && item.moreStopFlight.isBestOfCategory && classes.matrixBestPrice}>
                                                                {item.moreStopFlight ? `R$ ${item.moreStopFlight.price}` : "-"}
                                                            </Typography>
                                                        </strong>
                                                        {item.moreStopFlight && item.moreStopFlight.isBestOfGeneral && <Tag label="Melhor" className={classes.matrixBestPriceTag} icon={<Meal className={classes.matrixBestPriceIcon} />} />}
                                                    </>
                                                )}
                                            </span>
                                        }
                                    />
                                </Grid>
                            </Grid>
                        ))}
                    </Slider>
                </div>
            </Grid>
        </Box>
    );
};

Matrix.propTypes = {
    classes: PropTypes.object.isRequired,
    cias: PropTypes.array.isRequired,
    isLoading: PropTypes.bool,
    handleClick: PropTypes.func.isRequired
};

export default withStyles(styles, { withTheme: true })(Matrix);
