import Stars from "../../Stars";
import Price from "../../Price";
import TripAdvisor from "../../TripAdvisor";
import Amenities from "../../Amenities";
import "../../assets/css/slick-theme.css";

import { styles } from "./styles";
import Image from "../../Image/Image";

const uuidv4 = require("uuid/v4");

const fallbackImgUrl = "https://res.cloudinary.com/cvc-corp/image/upload/v1613574585/img-not-found/img-not-found-results.png";

const ResultItemHotel = ({
    classes,
    className,
    product,
    onClick,
    isLoading,
    type,
    imgs,
    notAvaliable,
    meal,
    onFav,
    title,
    stars,
    subtitle,
    rate,
    totalAvaliations,
    discount,
    amenities,
    stops,
    departure,
    arrival,
    hasLuggage,
    totalPrice,
    partialPrice,
    oldPartialPrice,
    totalPeople,
    totalDays,
    taxesIncluded,
    parcel,
    isFavorite,
    isActive,
    id,
    lastItem,
    hotelCoordinates,
    isAvuxi,
    toggleSeeModal
}) => {
    const NextArrow = props => {
        const handleClick = e => {
            if (props.onClick) props.onClick(e);
            e.stopPropagation();
        };
        return (
            <button type="button" className={clsx(classes.arrows, classes.arrowRight)} onClick={e => handleClick(e)}>
                <DropRight size="medium" />
            </button>
        );
    };

    const PrevArrow = props => {
        const handleClick = e => {
            if (props.onClick) props.onClick(e);
            e.stopPropagation();
        };
        return (
            <button type="button" className={clsx(classes.arrows, classes.arrowLeft)} onClick={e => handleClick(e)}>
                <DropLeft size="medium" />
            </button>
        );
    };

    const classNameBox = () => {
        if (type === "list") {
            if (isLoading) {
                return clsx(classes.root, classes.rootList, classes.rootLoading, className);
            }
            return clsx(classes.root, classes.rootList, className, isActive && classes.isActive);
        }
        return clsx(classes.root, classes[product], className);
    };

    return (
        <Box className={classNameBox()} onClick={onClick} id={id}>
            {!isLoading && (
                <div className={type === "list" ? classes.typeList : ""}>
                    {imgs && (
                        <div className={type === "card" ? classes.imgContainerCard : classes.imgContainerList}>
                            {imgs.length > 1 && <span className={clsx(classes.overlay, "overlay")} />}
                            <Slider infinite={false} speed={500} slidesToShow={1} touchThreshold={100} dots nextArrow={<NextArrow />} prevArrow={<PrevArrow />}>
                                {imgs.map((item, i) => {
                                    const fallbackImgs = [...imgs];
                                    fallbackImgs.splice(i, 1);
                                    return (
                                        <span className={type === "list" ? clsx(classes.imgWrapper, classes.imgWrapperList) : classes.imgWrapper} key={i}>
                                            <LazyLoad height={229} placeholder={<LoadingContent />}>
                                                <Image
                                                    className={notAvaliable ? clsx(classes.notAvaliableImg, classes.img) : classes.img}
                                                    src={item}
                                                    alt={item.title}
                                                    width={360}
                                                    height={360}
                                                    fallback={[...fallbackImgs, fallbackImgUrl]}
                                                ></Image>
                                            </LazyLoad>
                                        </span>
                                    );
                                })}
                            </Slider>
                        </div>
                    )}
                    {meal && !notAvaliable && <Tag className={imgs ? classes.topTag : ""} label={meal} icon={<Meal size="medium" className={classes.mealIcon} />} color="white" />}
                    {notAvaliable && <Tag className={imgs ? classes.topTag : ""} label="Hotel indisponível para estas datas" color="white" />}
                    {onFav && (
                        <span className={type === "list" ? clsx(classes.favorite, classes.favoriteList) : classes.favorite}>
                            <Favorite fill={isFavorite} size="medium" onClick={onFav} />
                        </span>
                    )}
                    <div className={type === "list" ? classes.content : ""}>
                        <div className={classes.header}>
                            <div className={classes.headerTitle}>
                                <Typography className={clsx(classes.title, "title")} type="headline6">
                                    {title}
                                </Typography>
                                {lastItem && <Tag className={`${classes.lastItem} ${type === "card" && classes.discountCard}`} label={lastItem} color="orange" />}
                            </div>
                            <Typography className={classes.subtitle} type="body2regular">
                                {stars > 1 && stars <= 5 && <Stars stars={stars} size="smaller" className={clsx("hotel-stars", classes.stars)} />}
                                {subtitle}
                            </Typography>
                            {isAvuxi && (
                                <Typography
                                    className={classes.verMapa}
                                    type="subtitle"
                                    onClick={ev => {
                                        toggleSeeModal(hotelCoordinates);
                                        ev.stopPropagation();
                                    }}
                                >
                                    Veja no mapa
                                </Typography>
                            )}
                        </div>
                        <div className={classes.info}>
                            {rate && <TripAdvisor className={type === "list" ? classes.tripAdvisorList : classes.tripAdvisor} totalAvaliations={totalAvaliations} rate={rate} />}
                            {discount && <Tag className={type === "list" ? classes.discountList : classes.discountCard} label={discount} color={product} />}
                        </div>
                        <Grid container alignItems="center">
                            <Grid item xs={7}>
                                {amenities && product === "hotel" && <Amenities amenities={amenities} amenitiesToShow={4} showTooltip />}
                                {product === "package" && (
                                    <div className={classes.flight}>
                                        <Typography type="captionregular">Voo {stops ? "com paradas" : "direto"} incluso</Typography>
                                        <Typography type="body1semibold" className={classes.flightInfo}>
                                            {departure}
                                            <Air />
                                            {arrival}
                                            <span className={classes.luggage}>
                                                <Luggage carryOn={!hasLuggage} />
                                            </span>
                                        </Typography>
                                    </div>
                                )}
                            </Grid>
                            {!notAvaliable && (
                                <Grid item xs={5}>
                                    <Price className={classes.priceContainer} product={product} totalPrice={totalPrice} partialPrice={partialPrice} oldPartialPrice={oldPartialPrice} totalPeople={totalPeople} totalDays={totalDays} />
                                </Grid>
                            )}
                        </Grid>
                        {(taxesIncluded || parcel) && (
                            <div className={classes.footer}>
                                {taxesIncluded && (
                                    <Typography className={classes.footerItem} type="captionregular">
                                        Taxas inclusas
                                    </Typography>
                                )}
                                {parcel && (
                                    <Typography className={classes.footerItem} type="captionsemibold">
                                        Em até {parcel}x
                                    </Typography>
                                )}
                            </div>
                        )}
                    </div>
                </div>
            )}
            {isLoading && (
                <>
                    <Grid container spacing={2} className={type === "list" && classes.loading}>
                        <Grid item xs={type === "list" ? 6 : 12} className={classes.relative}>
                            <div className={type === "card" ? classes.imgContainerCard : ""}>
                                <div className={classes.imgContainerPromoSmall}>
                                    <LoadingContent absolute />
                                </div>
                            </div>
                        </Grid>
                        <Grid item xs={type === "list" ? 6 : 12}>
                            <Grid container>
                                <Grid item xs={10}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                            <Grid container className={classes.loadingContent}>
                                <Grid item xs={5}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                            <Grid container className={classes.loadingContent}>
                                <Grid item xs={9}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                            <Grid container className={classes.loadingPrice}>
                                <Grid item xs={3}>
                                    <LoadingContent />
                                </Grid>
                            </Grid>
                        </Grid>
                    </Grid>
                </>
            )}
        </Box>
    );
};

ResultItemHotel.defaultProps = {
    product: "hotel",
    type: "list",
    imgs: [],
    isLoading: false,
    notAvaliable: false,
    isFavorite: false,
    taxesIncluded: false,
    onFav: undefined,
    onClick: () => {}
};

ResultItemHotel.prototype = {
    product: PropTypes.string,
    type: PropTypes.string,
    imgs: PropTypes.array,
    isLoading: PropTypes.bool,
    onClick: PropTypes.func,
    title: PropTypes.string,
    subtitle: PropTypes.string,
    meal: PropTypes.string,
    notAvaliable: PropTypes.bool,
    isFavorite: PropTypes.bool,
    rate: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    onFav: PropTypes.func,
    stars: PropTypes.number,
    totalDays: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
    partialPrice: PropTypes.string,
    totalPrice: PropTypes.string,
    amenities: PropTypes.array,
    taxesIncluded: PropTypes.bool,
    parcel: PropTypes.string,
    totalAvaliations: PropTypes.string,
    discount: PropTypes.string,
    departure: PropTypes.string,
    arrival: PropTypes.string,
    hasLuggage: PropTypes.bool,
    stops: PropTypes.bool,
    lastItem: PropTypes.string
};

export default withStyles(styles, { withTheme: true })(ResultItemHotel);
