import React from "react";
import PropTypes from "prop-types";
import MenuItem from "@material-ui/core/MenuItem";
import MenuList from "@material-ui/core/MenuList";
import withStyles from "@material-ui/core/styles/withStyles";
import Box from "../Box";
import { styles } from "./styles";

const Dropdown = ({ classes, selected, onClick, menu, className }) => {
    return (
        <Box className={`${classes.root} ${className}`}>
            <MenuList>
                {menu.map((item, index) => (
                    <MenuItem
                        onClick={() => onClick && onClick(item)}
                        // TODO verificar implementação
                        // selected é setado como bool nas propTypes
                        // mas é usado na comparação com strings
                        // falha: sempre retornará false
                        selected={selected === item}
                        key={index}
                    >
                        {item}
                    </MenuItem>
                ))}
            </MenuList>
        </Box>
    );
};

Dropdown.propTypes = {
    classes: PropTypes.object,
    menu: PropTypes.array.isRequired,
    className: PropTypes.string,
    onClick: PropTypes.func,
    selected: PropTypes.bool,
};

export default withStyles(styles, { withTheme: true })(Dropdown);
